import{_ as e,o as a,c as n,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 18.2-46.3:3 Enhanced punishment for gang activity taking place in a gang-free zone; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-46.3:3.md","filePath":"vacode/18.2-46.3:3.md"}'),r={name:"vacode/18.2-46.3:3.md"},t=i('<h1 id="§-18-2-46-3-3-enhanced-punishment-for-gang-activity-taking-place-in-a-gang-free-zone-penalties" tabindex="-1">§ 18.2-46.3:3 Enhanced punishment for gang activity taking place in a gang-free zone; penalties. <a class="header-anchor" href="#§-18-2-46-3-3-enhanced-punishment-for-gang-activity-taking-place-in-a-gang-free-zone-penalties" aria-label="Permalink to &quot;§ 18.2-46.3:3 Enhanced punishment for gang activity taking place in a gang-free zone; penalties.&quot;">​</a></h1><p>Any person who violates § <a href="http://law.lis.virginia.gov/vacode/18.2-46.2/" target="_blank" rel="noreferrer">18.2-46.2</a> (i) upon the property, including buildings and grounds, of any public or private elementary, secondary, or postsecondary school or institution of higher education; (ii) upon public property or any property open to public use within 1,000 feet of such school property; (iii) on any school bus as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-100/" target="_blank" rel="noreferrer">46.2-100</a>; or (iv) upon the property, including buildings and grounds, of any publicly owned or operated community center or any publicly owned or operated recreation center is guilty of a felony punishable as specified in § <a href="http://law.lis.virginia.gov/vacode/18.2-46.2/" target="_blank" rel="noreferrer">18.2-46.2</a>, and shall be sentenced to a mandatory minimum term of imprisonment of two years to be served consecutively with any other sentence. A person who violates subsection A of § <a href="http://law.lis.virginia.gov/vacode/18.2-46.3/" target="_blank" rel="noreferrer">18.2-46.3</a> upon any property listed in this section is guilty of a Class 6 felony, except that any person 18 years of age or older who violates subsection A of § <a href="http://law.lis.virginia.gov/vacode/18.2-46.3/" target="_blank" rel="noreferrer">18.2-46.3</a> upon any property listed in this section, when such offense is committed against a juvenile, is guilty of a Class 5 felony. Any person who violates subsection B of § <a href="http://law.lis.virginia.gov/vacode/18.2-46.3/" target="_blank" rel="noreferrer">18.2-46.3</a> upon any property listed in this section is guilty of a Class 5 felony. It is a violation of this section if the person violated § <a href="http://law.lis.virginia.gov/vacode/18.2-46.2/" target="_blank" rel="noreferrer">18.2-46.2</a> or <a href="http://law.lis.virginia.gov/vacode/18.2-46.3/" target="_blank" rel="noreferrer">18.2-46.3</a> on the property described in clauses (i) through (iii) regardless of where the person intended to commit such violation.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0764" target="_blank" rel="noreferrer">764</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0364" target="_blank" rel="noreferrer">364</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0761" target="_blank" rel="noreferrer">761</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>.</p>',3),o=[t];function l(s,c,p,g,h,f){return a(),n("div",null,o)}const u=e(r,[["render",l]]);export{v as __pageData,u as default};
