import{_ as a,o as t,c as e,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 18.2-46.7 Act of bioterrorism against agricultural crops or animals; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-46.7.md","filePath":"vacode/18.2-46.7.md"}'),o={name:"vacode/18.2-46.7.md"},i=r('<h1 id="§-18-2-46-7-act-of-bioterrorism-against-agricultural-crops-or-animals-penalty" tabindex="-1">§ 18.2-46.7 Act of bioterrorism against agricultural crops or animals; penalty. <a class="header-anchor" href="#§-18-2-46-7-act-of-bioterrorism-against-agricultural-crops-or-animals-penalty" aria-label="Permalink to &quot;§ 18.2-46.7 Act of bioterrorism against agricultural crops or animals; penalty.&quot;">​</a></h1><p>Any person who maliciously destroys or devastates agricultural crops or agricultural animals having a value of $2,500 or more through the use of an infectious biological substance with the intent to (i) intimidate the civilian population or (ii) influence the conduct or activities of the government of the United States, a state or locality through intimidation, is guilty of a Class 3 felony.</p><p>For the purposes of this section &quot;agricultural animal&quot; means all livestock and poultry as defined in § <a href="http://law.lis.virginia.gov/vacode/3.2-5900/" target="_blank" rel="noreferrer">3.2-5900</a> and &quot;agricultural crop&quot; means cultivated plants or produce, including grain, silage, forages, oilseeds, vegetables, fruits, nursery stock or turf grass.</p><p>2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0623" target="_blank" rel="noreferrer">623</a>.</p>',4),s=[i];function n(l,c,u,p,g,f){return t(),e("div",null,s)}const h=a(o,[["render",n]]);export{_ as __pageData,h as default};
