import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 18.2-473.1 Communication with prisoners or committed person; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-473.1.md","filePath":"vacode/18.2-473.1.md"}'),r={name:"vacode/18.2-473.1.md"},n=i('<h1 id="§-18-2-473-1-communication-with-prisoners-or-committed-person-penalty" tabindex="-1">§ 18.2-473.1 Communication with prisoners or committed person; penalty. <a class="header-anchor" href="#§-18-2-473-1-communication-with-prisoners-or-committed-person-penalty" aria-label="Permalink to &quot;§ 18.2-473.1 Communication with prisoners or committed person; penalty.&quot;">​</a></h1><p>It shall be unlawful for any person outside of any state or local correctional facility or any juvenile correctional center, other than the jailers or custodial officers in charge of the prisoners or in charge of the persons committed to the Department of Juvenile Justice, to communicate without authority by word or sign with the intent to disrupt institutional operations with any prisoner confined within a state or local correctional facility or with any person committed to the Department of Juvenile Justice in any juvenile correctional center. Any person violating this section is guilty of a Class 4 misdemeanor.</p><p>1982, c. 636; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0286" target="_blank" rel="noreferrer">286</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0707" target="_blank" rel="noreferrer">707</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0782" target="_blank" rel="noreferrer">782</a>.</p>',3),a=[n];function c(s,l,p,h,m,_){return t(),o("div",null,a)}const u=e(r,[["render",c]]);export{f as __pageData,u as default};
