import{_ as e,o as a,c as i,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 18.2-474.2 Bribery in correctional facilities; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-474.2.md","filePath":"vacode/18.2-474.2.md"}'),o={name:"vacode/18.2-474.2.md"},t=r('<h1 id="§-18-2-474-2-bribery-in-correctional-facilities-penalty" tabindex="-1">§ 18.2-474.2 Bribery in correctional facilities; penalty. <a class="header-anchor" href="#§-18-2-474-2-bribery-in-correctional-facilities-penalty" aria-label="Permalink to &quot;§ 18.2-474.2 Bribery in correctional facilities; penalty.&quot;">​</a></h1><p>A. Any person who receives any pecuniary benefit or other consideration to act in violation of § <a href="/vacode/18.2-474/">18.2-474</a> or <a href="/vacode/18.2-474.1/">18.2-474.1</a> is guilty of bribery, punishable as a Class 4 felony.</p><p>B. Any law-enforcement officer as defined in § <a href="/vacode/9.1-101/">9.1-101</a>, jail officer as defined in § <a href="/vacode/53.1-1/">53.1-1</a>, or correctional officer as defined in § <a href="/vacode/53.1-1/">53.1-1</a> who violates this section shall be decertified in accordance with § <a href="/vacode/15.2-1707/">15.2-1707</a>, if applicable, and shall be forever ineligible for reemployment as a law-enforcement officer, jail officer, or correctional officer in the Commonwealth.</p><p>2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0289" target="_blank" rel="noreferrer">289</a>.</p>',4),n=[t];function c(l,f,s,d,p,_){return a(),i("div",null,n)}const y=e(o,[["render",c]]);export{b as __pageData,y as default};
