import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 18.2-477.1 Escapes from juvenile facility; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-477.1.md","filePath":"vacode/18.2-477.1.md"}'),i={name:"vacode/18.2-477.1.md"},r=o('<h1 id="§-18-2-477-1-escapes-from-juvenile-facility-penalty" tabindex="-1">§ 18.2-477.1 Escapes from juvenile facility; penalty. <a class="header-anchor" href="#§-18-2-477-1-escapes-from-juvenile-facility-penalty" aria-label="Permalink to &quot;§ 18.2-477.1 Escapes from juvenile facility; penalty.&quot;">​</a></h1><p>A. It shall be unlawful for any person to escape or remain away without proper authority from a group home or other residential care facility for children in need of services, delinquent or alleged delinquent youths in which he had been placed by the juvenile and domestic relations court or as a result of his commitment as a juvenile to the Department of Juvenile Justice. Any person violating this subsection shall be taken into custody and brought before the juvenile and domestic relations court. The court may find the person in violation of § <a href="http://law.lis.virginia.gov/vacode/16.1-292/" target="_blank" rel="noreferrer">16.1-292</a> or, if the court finds the person amenable to further treatment in a juvenile facility, the court may return him to the custody of the Department.</p><p>B. It shall be unlawful for any person to escape or remain away without proper authority from a secure facility operated by or under contract with the Department of Juvenile Justice or from a secure juvenile detention facility in which he had been placed by the juvenile and domestic relations court or as a result of his commitment as a juvenile to the Department of Juvenile Justice. Any person who escapes from a facility specified in this subsection by force or by violence shall be guilty of a Class 6 felony or, if violation of this subsection occurs other than by force or violence, a Class 1 misdemeanor.</p><p>1985, c. 435; 1989, c. 733; 1993, c. 840; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0490" target="_blank" rel="noreferrer">490</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0749" target="_blank" rel="noreferrer">749</a>.</p>',4),n=[r];function l(s,c,h,f,u,p){return t(),a("div",null,n)}const y=e(i,[["render",l]]);export{m as __pageData,y as default};
