import{_ as t,o,c as s,z as e,a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 18.2-480.1 Admissibility of records of Department of Corrections in escape cases.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-480.1.md","filePath":"vacode/18.2-480.1.md"}'),r={name:"vacode/18.2-480.1.md"},i=e("h1",{id:"§-18-2-480-1-admissibility-of-records-of-department-of-corrections-in-escape-cases",tabindex:"-1"},[a("§ 18.2-480.1 Admissibility of records of Department of Corrections in escape cases. "),e("a",{class:"header-anchor",href:"#§-18-2-480-1-admissibility-of-records-of-department-of-corrections-in-escape-cases","aria-label":'Permalink to "§ 18.2-480.1 Admissibility of records of Department of Corrections in escape cases."'},"​")],-1),n=e("p",null,"In any prosecution for, or preliminary hearing for, the offense of escape under this article or Title 53.1, the records maintained by the Department of Corrections or the Department of Juvenile Justice, when such records are duly attested by the custodian of such records, shall be admissible in evidence as evidence of the fact, location and dates of confinement, provided that the records shall be filed with the clerk of the court hearing the case at least seven days prior to the trial or preliminary hearing. On motion of the accused, the court may require the custodian to appear as a witness and be subject to cross-examination; provided such motion is made within a reasonable time prior to the day on which the case is set for trial; and provided further, that the custodian so appearing shall be considered the Commonwealth's witness.",-1),c=e("p",null,"1976, c. 394; 1989, c. 733.",-1),d=[i,n,c];function h(l,f,p,m,u,_){return o(),s("div",null,d)}const v=t(r,[["render",h]]);export{y as __pageData,v as default};
