import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-488.1 Flag at half staff or mast for certain public safety personnel killed in the line of duty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-488.1.md","filePath":"vacode/18.2-488.1.md"}'),i={name:"vacode/18.2-488.1.md"},o=r('<h1 id="§-18-2-488-1-flag-at-half-staff-or-mast-for-certain-public-safety-personnel-killed-in-the-line-of-duty" tabindex="-1">§ 18.2-488.1 Flag at half staff or mast for certain public safety personnel killed in the line of duty. <a class="header-anchor" href="#§-18-2-488-1-flag-at-half-staff-or-mast-for-certain-public-safety-personnel-killed-in-the-line-of-duty" aria-label="Permalink to &quot;§ 18.2-488.1 Flag at half staff or mast for certain public safety personnel killed in the line of duty.&quot;">​</a></h1><p>A. As used in this section, unless the context requires a different meaning:</p><p>&quot;Emergency medical services provider&quot; means the same as that term is defined in § <a href="http://law.lis.virginia.gov/vacode/32.1-111.1/" target="_blank" rel="noreferrer">32.1-111.1</a> and any member of a volunteer emergency medical services agency.</p><p>&quot;Firefighter&quot; means the same as that term is defined in § <a href="http://law.lis.virginia.gov/vacode/9.1-300/" target="_blank" rel="noreferrer">9.1-300</a>, and any member of a volunteer fire department.</p><p>&quot;Police officer&quot; means any full-time or part-time employee of a police department or sheriff&#39;s office which is a part of or administered by the Commonwealth or any political subdivision thereof and who is responsible for the prevention and detection of crime and the enforcement of the penal, traffic, or highway laws of the Commonwealth and a state correctional officer of the Department of Corrections.</p><p>&quot;Service member&quot; means a member of the United States armed forces, Virginia National Guard, or Virginia Defense Force.</p><p>B. Whenever a service member, police officer, firefighter, or emergency medical services provider who is a resident of Virginia is killed in the line of duty, all flags, state and local, flown at any building owned and operated by the Commonwealth or any political subdivision thereof shall be flown at half staff or mast for one day to honor and acknowledge respect for those who made the supreme sacrifice.</p><p>C. The Department of General Services shall develop procedures to effectuate the purposes of this section.</p><p>2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0344" target="_blank" rel="noreferrer">344</a>.</p>',9),n=[o];function l(f,s,c,h,d,p){return a(),t("div",null,n)}const u=e(i,[["render",l]]);export{g as __pageData,u as default};
