import{_ as e,o,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-51.7 Female genital mutilation; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-51.7.md","filePath":"vacode/18.2-51.7.md"}'),t={name:"vacode/18.2-51.7.md"},r=i('<h1 id="§-18-2-51-7-female-genital-mutilation-penalty" tabindex="-1">§ 18.2-51.7 Female genital mutilation; penalty. <a class="header-anchor" href="#§-18-2-51-7-female-genital-mutilation-penalty" aria-label="Permalink to &quot;§ 18.2-51.7 Female genital mutilation; penalty.&quot;">​</a></h1><p>A. Any person who knowingly circumcises, excises, or infibulates, in whole or in any part, the labia majora or labia minora or clitoris of another person who has not attained the age of 18 years is guilty of a Class 2 felony.</p><p>B. Any parent, guardian, or other person responsible for the care of a minor who consents to the circumcision, excision, or infibulation, in whole or in any part, of the labia majora or labia minora or clitoris of such minor is guilty of a Class 2 felony.</p><p>C. Any parent, guardian, or other person responsible for the care of a minor who knowingly removes or causes or permits the removal of such minor from the Commonwealth for the purposes of committing an offense under subsection A is guilty of a Class 2 felony.</p><p>D. A surgical operation is not a violation of this section if the operation is (i) necessary to the health of the person on whom it is performed and is performed by a person licensed in the place of its performance as a medical practitioner or (ii) performed on a person in labor who has just given birth and is performed for medical purposes connected with that labor or birth by a person licensed in the place it is performed as a medical practitioner, midwife, or person in training to become such a practitioner or midwife.</p><p>E. A violation of this section shall constitute a separate and distinct offense. The provisions of this section shall not preclude prosecution under any other statute.</p><p>2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0667" target="_blank" rel="noreferrer">667</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0549" target="_blank" rel="noreferrer">549</a>.</p>',7),n=[r];function s(l,c,p,h,f,m){return o(),a("div",null,n)}const _=e(t,[["render",s]]);export{u as __pageData,_ as default};
