import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-514 Racketeering offenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-514.md","filePath":"vacode/18.2-514.md"}'),a={name:"vacode/18.2-514.md"},n=t('<h1 id="§-18-2-514-racketeering-offenses" tabindex="-1">§ 18.2-514 Racketeering offenses. <a class="header-anchor" href="#§-18-2-514-racketeering-offenses" aria-label="Permalink to &quot;§ 18.2-514 Racketeering offenses.&quot;">​</a></h1><p>A. It shall be unlawful for an enterprise, or for any person who occupies a position of organizer, supervisor, or manager of an enterprise, to receive any proceeds known to have been derived directly from racketeering activity and to use or invest an aggregate of $10,000 or more of such proceeds in the acquisition of any title to, or any right, interest, or equity in, real property, or in the establishment or operation of any enterprise.</p><p>B. It shall be unlawful for any enterprise, or for any person who occupies a position of organizer, supervisor, or manager of an enterprise, to directly acquire or maintain any interest in or control of any enterprise or real property through racketeering activity.</p><p>C. It shall be unlawful for any person employed by, or associated with, any enterprise to conduct or participate, directly or indirectly, in such enterprise through racketeering activity.</p><p>D. It shall be unlawful for any person to conspire to violate any of the provisions of subsection A, B, or C.</p><p>E. Each violation of this section is a separate and distinct felony punishable in accordance with § <a href="http://law.lis.virginia.gov/vacode/18.2-515/" target="_blank" rel="noreferrer">18.2-515</a>.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0883" target="_blank" rel="noreferrer">883</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0847" target="_blank" rel="noreferrer">847</a>.</p>',7),i=[n];function s(c,l,p,f,h,g){return r(),o("div",null,i)}const _=e(a,[["render",s]]);export{u as __pageData,_ as default};
