import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 18.2-516 Prohibition of illegal money transmitting.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-516.md","filePath":"vacode/18.2-516.md"}'),a={name:"vacode/18.2-516.md"},n=i('<h1 id="§-18-2-516-prohibition-of-illegal-money-transmitting" tabindex="-1">§ 18.2-516 Prohibition of illegal money transmitting. <a class="header-anchor" href="#§-18-2-516-prohibition-of-illegal-money-transmitting" aria-label="Permalink to &quot;§ 18.2-516 Prohibition of illegal money transmitting.&quot;">​</a></h1><p>A. Any person who controls, manages, or owns all or part of an enterprise, engaged in money transmission as defined in § <a href="http://law.lis.virginia.gov/vacode/6.2-1900/" target="_blank" rel="noreferrer">6.2-1900</a>, and transmits money, which he knows or should have known was derived from or traceable to racketeering activity, is guilty of a Class 6 felony.</p><p>B. All property, real or personal, including money, used in substantial connection with, intended for use in the course of, or traceable to, conduct in violation of any provision of subsection A is subject to civil forfeiture to the Commonwealth. The forfeiture proceeding shall be conducted pursuant to the provisions of Chapter 22.1 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-386.1/" target="_blank" rel="noreferrer">19.2-386.1</a> et seq.) of Title 19.2.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0883" target="_blank" rel="noreferrer">883</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>.</p>',4),r=[n];function l(s,c,f,h,d,p){return o(),t("div",null,r)}const m=e(a,[["render",l]]);export{_ as __pageData,m as default};
