import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-517 Venue for prosecution.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-517.md","filePath":"vacode/18.2-517.md"}'),a={name:"vacode/18.2-517.md"},i=t('<h1 id="§-18-2-517-venue-for-prosecution" tabindex="-1">§ 18.2-517 Venue for prosecution. <a class="header-anchor" href="#§-18-2-517-venue-for-prosecution" aria-label="Permalink to &quot;§ 18.2-517 Venue for prosecution.&quot;">​</a></h1><p>For the purposes of venue, any violation of this chapter shall be considered to have been committed in any county or city:</p><ol><li><p>In which any act was performed in furtherance of any course of conduct that violates this chapter;</p></li><li><p>That is the principal place of the enterprise in the Commonwealth;</p></li><li><p>In which any offender had control or possession of any proceeds of a violation of this chapter, or of any records, or any other material or objects, which were used in furtherance of a violation;</p></li><li><p>In which any offender resides; or</p></li><li><p>Any place of venue under Article 2 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-244/" target="_blank" rel="noreferrer">19.2-244</a> et seq.) of Chapter 15 of Title 19.2.</p></li></ol><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0883" target="_blank" rel="noreferrer">883</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0996" target="_blank" rel="noreferrer">996</a>.</p>',4),n=[i];function c(s,l,p,h,f,d){return o(),r("div",null,n)}const v=e(a,[["render",c]]);export{u as __pageData,v as default};
