import{_ as e,o as i,c as a,O as o}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 18.2-52.1 Possession of infectious biological substances or radiological agents; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-52.1.md","filePath":"vacode/18.2-52.1.md"}'),t={name:"vacode/18.2-52.1.md"},s=o('<h1 id="§-18-2-52-1-possession-of-infectious-biological-substances-or-radiological-agents-penalties" tabindex="-1">§ 18.2-52.1 Possession of infectious biological substances or radiological agents; penalties. <a class="header-anchor" href="#§-18-2-52-1-possession-of-infectious-biological-substances-or-radiological-agents-penalties" aria-label="Permalink to &quot;§ 18.2-52.1 Possession of infectious biological substances or radiological agents; penalties.&quot;">​</a></h1><p>A. Any person who possesses, with the intent thereby to injure another, an infectious biological substance or radiological agent is guilty of a Class 5 felony.</p><p>B. Any person who (i) destroys or damages, or attempts to destroy or damage, any facility, equipment or material involved in the sale, manufacturing, storage or distribution of an infectious biological substance or radiological agent, with the intent to injure another by releasing the substance, or (ii) manufactures, sells, gives, distributes or uses an infectious biological substance or radiological agent with the intent to injure another is guilty of a Class 4 felony.</p><p>C. Any person who maliciously and intentionally causes any other person bodily injury by means of an infectious biological substance or radiological agent is guilty of a felony and shall be punished by confinement in a state correctional facility for a period of not less than five years nor more than 30 years.</p><p>D. For purposes of this section:</p><p>An &quot;infectious biological substance&quot; includes any bacteria, viruses, fungi, protozoa, or rickettsiae capable of causing death or serious bodily injury. &quot;Infectious biological substance&quot; does not include the human immunodeficiency virus (HIV) or any other related virus that causes acquired immunodeficiency syndrome (AIDS), syphilis, or hepatitis B.</p><p>A &quot;radiological agent&quot; includes any substance able to release radiation at levels that are capable of causing death or serious bodily injury.</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0769" target="_blank" rel="noreferrer">769</a>; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0623" target="_blank" rel="noreferrer">623</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0833" target="_blank" rel="noreferrer">833</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0465" target="_blank" rel="noreferrer">465</a>.</p>',8),n=[s];function r(l,c,u,g,f,p){return i(),a("div",null,n)}const b=e(t,[["render",r]]);export{h as __pageData,b as default};
