import{_ as e,o as a,c as i,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-53.1 Use or display of firearm in committing felony.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-53.1.md","filePath":"vacode/18.2-53.1.md"}'),o={name:"vacode/18.2-53.1.md"},n=r('<h1 id="§-18-2-53-1-use-or-display-of-firearm-in-committing-felony" tabindex="-1">§ 18.2-53.1 Use or display of firearm in committing felony. <a class="header-anchor" href="#§-18-2-53-1-use-or-display-of-firearm-in-committing-felony" aria-label="Permalink to &quot;§ 18.2-53.1 Use or display of firearm in committing felony.&quot;">​</a></h1><p>It shall be unlawful for any person to use or attempt to use any pistol, shotgun, rifle, or other firearm or display such weapon in a threatening manner while committing or attempting to commit murder, rape, forcible sodomy, inanimate or animate object sexual penetration as defined in § <a href="http://law.lis.virginia.gov/vacode/18.2-67.2/" target="_blank" rel="noreferrer">18.2-67.2</a>, robbery, carjacking, burglary, malicious wounding as defined in § <a href="http://law.lis.virginia.gov/vacode/18.2-51/" target="_blank" rel="noreferrer">18.2-51</a>, malicious bodily injury to a law-enforcement officer as defined in § <a href="http://law.lis.virginia.gov/vacode/18.2-51.1/" target="_blank" rel="noreferrer">18.2-51.1</a>, aggravated malicious wounding as defined in § <a href="http://law.lis.virginia.gov/vacode/18.2-51.2/" target="_blank" rel="noreferrer">18.2-51.2</a>, malicious wounding by mob as defined in § <a href="http://law.lis.virginia.gov/vacode/18.2-41/" target="_blank" rel="noreferrer">18.2-41</a> or abduction. Violation of this section shall constitute a separate and distinct felony and any person found guilty thereof shall be sentenced to a mandatory minimum term of imprisonment of three years for a first conviction, and to a mandatory minimum term of five years for a second or subsequent conviction under the provisions of this section. Such punishment shall be separate and apart from, and shall be made to run consecutively with, any punishment received for the commission of the primary felony.</p><p>1975, cc. 624, 628; 1976, c. 371; 1980, c. 333; 1982, c. 654; 1991, c. 506; 1992, cc. 191, 726; 1993, cc. 549, 835; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0950" target="_blank" rel="noreferrer">950</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0461" target="_blank" rel="noreferrer">461</a>.</p>',3),t=[n];function s(l,c,f,d,m,h){return a(),i("div",null,t)}const u=e(o,[["render",s]]);export{g as __pageData,u as default};
