import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 18.2-55.1 Hazing of youth gang members unlawful; criminal liability.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-55.1.md","filePath":"vacode/18.2-55.1.md"}'),t={name:"vacode/18.2-55.1.md"},o=n('<h1 id="§-18-2-55-1-hazing-of-youth-gang-members-unlawful-criminal-liability" tabindex="-1">§ 18.2-55.1 Hazing of youth gang members unlawful; criminal liability. <a class="header-anchor" href="#§-18-2-55-1-hazing-of-youth-gang-members-unlawful-criminal-liability" aria-label="Permalink to &quot;§ 18.2-55.1 Hazing of youth gang members unlawful; criminal liability.&quot;">​</a></h1><p>It shall be unlawful to cause bodily injury by hazing (i) any member of a criminal street gang as defined in § <a href="http://law.lis.virginia.gov/vacode/18.2-46.1/" target="_blank" rel="noreferrer">18.2-46.1</a>, or (ii) a person seeking to become a member of a youth gang or criminal street gang. Any person found guilty of hazing is guilty of a Class 1 misdemeanor.</p><p>For the purposes of this section, &quot;hazing&quot; means to recklessly or intentionally endanger the health or safety of a person or to inflict bodily injury on a person in connection with or for the purpose of initiation, admission into or affiliation with or as a condition for continued membership in a youth gang or criminal street gang regardless of whether the person so endangered or injured participated voluntarily in the relevant activity.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0850" target="_blank" rel="noreferrer">850</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0843" target="_blank" rel="noreferrer">843</a>.</p>',4),r=[o];function l(s,c,g,f,h,u){return a(),i("div",null,r)}const p=e(t,[["render",l]]);export{d as __pageData,p as default};
