import{_ as e,o as r,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-55 Bodily injuries caused by prisoners, state juvenile probationers and state and local adult probationers or adult parolees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-55.md","filePath":"vacode/18.2-55.md"}'),o={name:"vacode/18.2-55.md"},t=i('<h1 id="§-18-2-55-bodily-injuries-caused-by-prisoners-state-juvenile-probationers-and-state-and-local-adult-probationers-or-adult-parolees" tabindex="-1">§ 18.2-55 Bodily injuries caused by prisoners, state juvenile probationers and state and local adult probationers or adult parolees. <a class="header-anchor" href="#§-18-2-55-bodily-injuries-caused-by-prisoners-state-juvenile-probationers-and-state-and-local-adult-probationers-or-adult-parolees" aria-label="Permalink to &quot;§ 18.2-55 Bodily injuries caused by prisoners, state juvenile probationers and state and local adult probationers or adult parolees.&quot;">​</a></h1><p>A. It shall be unlawful for a person confined in a state, local or regional correctional facility as defined in § <a href="http://law.lis.virginia.gov/vacode/53.1-1/" target="_blank" rel="noreferrer">53.1-1</a>; in a secure facility or detention home as defined in § <a href="http://law.lis.virginia.gov/vacode/16.1-228/" target="_blank" rel="noreferrer">16.1-228</a> or in any facility designed for the secure detention of juveniles; or while in the custody of an employee thereof to knowingly and willfully inflict bodily injury on:</p><ol><li><p>An employee thereof, or</p></li><li><p>Any other person lawfully admitted to such facility, except another prisoner or person held in legal custody, or</p></li><li><p>Any person who is supervising or working with prisoners or persons held in legal custody, or</p></li><li><p>Any such employee or other person while such prisoner or person held in legal custody is committing any act in violation of § <a href="http://law.lis.virginia.gov/vacode/53.1-203/" target="_blank" rel="noreferrer">53.1-203</a>.</p></li></ol><p>B. It shall be unlawful for an accused, probationer or parolee under the supervision of, or being investigated by, (i) a probation or parole officer whose powers and duties are defined in § <a href="http://law.lis.virginia.gov/vacode/16.1-237/" target="_blank" rel="noreferrer">16.1-237</a> or § <a href="http://law.lis.virginia.gov/vacode/53.1-145/" target="_blank" rel="noreferrer">53.1-145</a>, (ii) a local pretrial services officer associated with an agency established pursuant to Article 5 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-152.2/" target="_blank" rel="noreferrer">19.2-152.2</a>) of Chapter 9 of Title 19.2, or (iii) a local community-based probation officer associated with an agency established pursuant to Article 9 (§ <a href="http://law.lis.virginia.gov/vacode/9.1-173/" target="_blank" rel="noreferrer">9.1-173</a> et seq.) of Chapter 1 of Title 9.1, to knowingly and willfully inflict bodily injury on such officer while he is in the performance of his duty, knowing or having reason to know that the officer is engaged in the performance of his duty.</p><p>Any person violating any provision of this section is guilty of a Class 5 felony.</p><p>1975, cc. 14, 15; 1977, c. 553; 1982, c. 636; 1985, c. 508; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0527" target="_blank" rel="noreferrer">527</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0618" target="_blank" rel="noreferrer">618</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0658" target="_blank" rel="noreferrer">658</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0818" target="_blank" rel="noreferrer">818</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0848" target="_blank" rel="noreferrer">848</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0133" target="_blank" rel="noreferrer">133</a>.</p>',6),n=[t];function l(s,c,p,d,f,h){return r(),a("div",null,n)}const v=e(o,[["render",l]]);export{u as __pageData,v as default};
