import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-57.2 Assault and battery against a family or household member; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-57.2.md","filePath":"vacode/18.2-57.2.md"}'),t={name:"vacode/18.2-57.2.md"},o=i('<h1 id="§-18-2-57-2-assault-and-battery-against-a-family-or-household-member-penalty" tabindex="-1">§ 18.2-57.2 Assault and battery against a family or household member; penalty. <a class="header-anchor" href="#§-18-2-57-2-assault-and-battery-against-a-family-or-household-member-penalty" aria-label="Permalink to &quot;§ 18.2-57.2 Assault and battery against a family or household member; penalty.&quot;">​</a></h1><p>A. Any person who commits an assault and battery against a family or household member is guilty of a Class 1 misdemeanor.</p><p>B. Upon a conviction for assault and battery against a family or household member, where it is alleged in the warrant, petition, information, or indictment on which a person is convicted, that such person has been previously convicted of two offenses against a family or household member of (i) assault and battery against a family or household member in violation of this section, (ii) malicious wounding or unlawful wounding in violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-51/" target="_blank" rel="noreferrer">18.2-51</a>, (iii) aggravated malicious wounding in violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-51.2/" target="_blank" rel="noreferrer">18.2-51.2</a>, (iv) malicious bodily injury by means of a substance in violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-52/" target="_blank" rel="noreferrer">18.2-52</a>, (v) strangulation in violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-51.6/" target="_blank" rel="noreferrer">18.2-51.6</a>, or (vi) an offense under the law of any other jurisdiction which has the same elements of any of the above offenses, in any combination, all of which occurred within a period of 20 years, and each of which occurred on a different date, such person is guilty of a Class 6 felony.</p><p>C. Whenever a warrant for a violation of this section is issued, the magistrate shall issue an emergency protective order as authorized by § <a href="http://law.lis.virginia.gov/vacode/16.1-253.4/" target="_blank" rel="noreferrer">16.1-253.4</a>, except if the defendant is a minor, an emergency protective order shall not be required.</p><p>D. The definition of &quot;family or household member&quot; in § <a href="http://law.lis.virginia.gov/vacode/16.1-228/" target="_blank" rel="noreferrer">16.1-228</a> applies to this section.</p><p>1991, c. 238; 1992, cc. 526, 886; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0866" target="_blank" rel="noreferrer">866</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0603" target="_blank" rel="noreferrer">603</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0697" target="_blank" rel="noreferrer">697</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0721" target="_blank" rel="noreferrer">721</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0807" target="_blank" rel="noreferrer">807</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0448" target="_blank" rel="noreferrer">448</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0738" target="_blank" rel="noreferrer">738</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0726" target="_blank" rel="noreferrer">726</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0660" target="_blank" rel="noreferrer">660</a>.</p>',6),n=[o];function l(s,h,f,g,c,d){return a(),r("div",null,n)}const v=e(t,[["render",l]]);export{u as __pageData,v as default};
