import{_ as e,o,c as a,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 18.2-58 Robbery; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-58.md","filePath":"vacode/18.2-58.md"}'),n={name:"vacode/18.2-58.md"},r=i('<h1 id="§-18-2-58-robbery-penalties" tabindex="-1">§ 18.2-58 Robbery; penalties. <a class="header-anchor" href="#§-18-2-58-robbery-penalties" aria-label="Permalink to &quot;§ 18.2-58 Robbery; penalties.&quot;">​</a></h1><p>A. For the purposes of this section, &quot;serious bodily injury&quot; means the same as that term is defined in § <a href="/vacode/18.2-51.4/">18.2-51.4</a>.</p><p>B. Any person who commits robbery is guilty of a felony and shall be punished as follows:</p><ol><li><p>Any person who commits robbery and causes serious bodily injury to or the death of any other person is guilty of a Class 2 felony.</p></li><li><p>Any person who commits robbery by using or displaying a firearm, as defined in § <a href="/vacode/18.2-308.2:2/">18.2-308.2:2</a>, in a threatening manner is guilty of a Class 3 felony.</p></li><li><p>Any person who commits robbery by using physical force not resulting in serious bodily injury or by using or displaying a deadly weapon other than a firearm in a threatening manner is guilty of a Class 5 felony.</p></li><li><p>Any person who commits robbery by using threat or intimidation or any other means not involving a deadly weapon is guilty of a Class 6 felony.</p></li></ol><p>Code 1950, § 18.1-91; 1960, c. 358; 1966, c. 361; 1975, cc. 14, 15, 605; 1978, c. 608; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0534" target="_blank" rel="noreferrer">534</a>.</p>',5),s=[r];function t(l,p,c,y,d,h){return o(),a("div",null,s)}const _=e(n,[["render",t]]);export{f as __pageData,_ as default};
