import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-59 Extortion of money, property or pecuniary benefit.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-59.md","filePath":"vacode/18.2-59.md"}'),i={name:"vacode/18.2-59.md"},n=t('<h1 id="§-18-2-59-extortion-of-money-property-or-pecuniary-benefit" tabindex="-1">§ 18.2-59 Extortion of money, property or pecuniary benefit. <a class="header-anchor" href="#§-18-2-59-extortion-of-money-property-or-pecuniary-benefit" aria-label="Permalink to &quot;§ 18.2-59 Extortion of money, property or pecuniary benefit.&quot;">​</a></h1><p>Any person who (i) threatens injury to the character, person, or property of another person, (ii) accuses him of any offense, (iii) threatens to report him as being illegally present in the United States, or (iv) knowingly destroys, conceals, removes, confiscates, withholds or threatens to withhold, or possesses any actual or purported passport or other immigration document, or any other actual or purported government identification document, of another person, and thereby extorts money, property, or pecuniary benefit or any note, bond, or other evidence of debt from him or any other person, is guilty of a Class 5 felony.</p><p>For the purposes of this section, injury to property includes the sale, distribution, or release of identifying information defined in clauses (iii) through (xii) of subsection C of § <a href="http://law.lis.virginia.gov/vacode/18.2-186.3/" target="_blank" rel="noreferrer">18.2-186.3</a>, but does not include the distribution or release of such information by a person who does so with the intent to obtain money, property or a pecuniary benefit to which he reasonably believes he is lawfully entitled.</p><p>Code 1950, § 18.1-184; 1960, c. 358; 1975, cc. 14, 15; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0313" target="_blank" rel="noreferrer">313</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0453" target="_blank" rel="noreferrer">453</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0547" target="_blank" rel="noreferrer">547</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0298" target="_blank" rel="noreferrer">298</a>.</p>',4),a=[n];function s(p,c,l,f,h,d){return o(),r("div",null,a)}const _=e(i,[["render",s]]);export{u as __pageData,_ as default};
