import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 18.2-6 Meaning of certain terms.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-6.md","filePath":"vacode/18.2-6.md"}'),r={name:"vacode/18.2-6.md"},i=o('<h1 id="§-18-2-6-meaning-of-certain-terms" tabindex="-1">§ 18.2-6 Meaning of certain terms. <a class="header-anchor" href="#§-18-2-6-meaning-of-certain-terms" aria-label="Permalink to &quot;§ 18.2-6 Meaning of certain terms.&quot;">​</a></h1><p>As used in this title:</p><p>The word &quot;court,&quot; unless otherwise clearly indicated by the context in which it appears, shall mean and include any court vested with appropriate jurisdiction under the Constitution and laws of the Commonwealth.</p><p>The words &quot;driver&#39;s license&quot; and &quot;license to operate a motor vehicle&quot; shall mean any document issued under Chapter 3 (§ <a href="/vacode/46.2-300/">46.2-300</a> et seq.) of Title 46.2, or the comparable law of another jurisdiction, authorizing the operation of a motor vehicle upon the highways.</p><p>The word &quot;judge,&quot; unless otherwise clearly indicated by the context in which it appears, shall mean and include any judge, associate judge or substitute judge, or police justice, of any court.</p><p>The words &quot;motor vehicle,&quot; &quot;semitrailer,&quot; &quot;trailer&quot; and &quot;vehicle&quot; shall have the respective meanings assigned to them by § <a href="/vacode/46.2-100/">46.2-100</a>.</p><p>Code 1950, § 18.1-5; 1960, c. 358; 1975, cc. 14, 15; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1227" target="_blank" rel="noreferrer">1227</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1246" target="_blank" rel="noreferrer">1246</a>.</p>',7),n=[i];function s(c,l,h,d,u,p){return t(),a("div",null,n)}const f=e(r,[["render",s]]);export{m as __pageData,f as default};
