import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 18.2-60.5 Unauthorized use of electronic tracking device; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-60.5.md","filePath":"vacode/18.2-60.5.md"}'),i={name:"vacode/18.2-60.5.md"},r=t('<h1 id="§-18-2-60-5-unauthorized-use-of-electronic-tracking-device-penalty" tabindex="-1">§ 18.2-60.5 Unauthorized use of electronic tracking device; penalty. <a class="header-anchor" href="#§-18-2-60-5-unauthorized-use-of-electronic-tracking-device-penalty" aria-label="Permalink to &quot;§ 18.2-60.5 Unauthorized use of electronic tracking device; penalty.&quot;">​</a></h1><p>A. Any person who installs or places an electronic tracking device through intentionally deceptive means and without consent, or causes an electronic tracking device to be installed or placed through intentionally deceptive means and without consent, and uses such device to track the location of any person is guilty of a Class 1 misdemeanor.</p><p>B. The provisions of this section shall not apply to the installation, placement, or use of an electronic tracking device by:</p><ol><li><p>A law-enforcement officer, judicial officer, probation or parole officer, or employee of the Department of Corrections when any such person is engaged in the lawful performance of official duties and in accordance with other state or federal law;</p></li><li><p>The parent or legal guardian of a minor when tracking (i) the minor or (ii) any person authorized by the parent or legal guardian as a caretaker of the minor at any time when the minor is under the person&#39;s sole care;</p></li><li><p>A legally authorized representative of a vulnerable adult, as defined in § <a href="/vacode/18.2-369/">18.2-369</a>;</p></li><li><p>The owner of fleet vehicles, when tracking such vehicles;</p></li><li><p>An electronic communications provider to the extent that such installation, placement, or use is disclosed in the provider&#39;s terms of use, privacy policy, or similar document made available to the customer; or</p></li><li><p>A registered private investigator, as defined in § <a href="/vacode/9.1-138/">9.1-138</a>, who is regulated in accordance with § <a href="/vacode/9.1-139/">9.1-139</a> and is acting in the normal course of his business and with the consent of the owner of the property upon which the electronic tracking device is installed and placed. However, such exception shall not apply if the private investigator is working on behalf of a client who is subject to a protective order under § <a href="/vacode/16.1-253/">16.1-253</a>, <a href="/vacode/16.1-253.1/">16.1-253.1</a>, <a href="/vacode/16.1-253.4/">16.1-253.4</a>, <a href="/vacode/16.1-279.1/">16.1-279.1</a>, <a href="/vacode/19.2-152.8/">19.2-152.8</a>, <a href="/vacode/19.2-152.9/">19.2-152.9</a>, or <a href="/vacode/19.2-152.10/">19.2-152.10</a> or subsection B of § <a href="/vacode/20-103/">20-103</a>, or if the private investigator knows or should reasonably know that the client seeks the private investigator&#39;s services to aid in the commission of a crime.</p></li></ol><p>C. For the purposes of this section:</p><p>&quot;Electronic tracking device&quot; means an electronic or mechanical device that permits a person to remotely determine or track the position and movement of another person.</p><p>&quot;Fleet vehicle&quot; means (i) one or more motor vehicles owned by a single entity and operated by employees or agents of the entity for business or government purposes, (ii) motor vehicles held for lease or rental to the general public, or (iii) motor vehicles held for sale by motor vehicle dealers.</p><p>2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0434" target="_blank" rel="noreferrer">434</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0140" target="_blank" rel="noreferrer">140</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0259" target="_blank" rel="noreferrer">259</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0642" target="_blank" rel="noreferrer">642</a>.</p>',8),n=[r];function c(l,s,h,d,p,f){return o(),a("div",null,n)}const g=e(i,[["render",c]]);export{v as __pageData,g as default};
