import{_ as e,o as t,c as n,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 18.2-67.10 General definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-67.10.md","filePath":"vacode/18.2-67.10.md"}'),i={name:"vacode/18.2-67.10.md"},o=a('<h1 id="§-18-2-67-10-general-definitions" tabindex="-1">§ 18.2-67.10 General definitions. <a class="header-anchor" href="#§-18-2-67-10-general-definitions" aria-label="Permalink to &quot;§ 18.2-67.10 General definitions.&quot;">​</a></h1><p>As used in this article:</p><ol><li><p>&quot;Complaining witness&quot; means the person alleged to have been subjected to rape, forcible sodomy, inanimate or animate object sexual penetration, marital sexual assault, aggravated sexual battery, or sexual battery.</p></li><li><p>&quot;Intimate parts&quot; means the genitalia, anus, groin, breast, or buttocks of any person, or the chest of a child under the age of 15.</p></li><li><p>&quot;Mental incapacity&quot; means that condition of the complaining witness existing at the time of an offense under this article which prevents the complaining witness from understanding the nature or consequences of the sexual act involved in such offense and about which the accused knew or should have known.</p></li><li><p>&quot;Physical helplessness&quot; means unconsciousness or any other condition existing at the time of an offense under this article which otherwise rendered the complaining witness physically unable to communicate an unwillingness to act and about which the accused knew or should have known.</p></li><li><p>The complaining witness&#39;s &quot;prior sexual conduct&quot; means any sexual conduct on the part of the complaining witness which took place before the conclusion of the trial, excluding the conduct involved in the offense alleged under this article.</p></li><li><p>&quot;Sexual abuse&quot; means an act committed with the intent to sexually molest, arouse, or gratify any person, where:</p></li></ol><p>a. The accused intentionally touches the complaining witness&#39;s intimate parts or material directly covering such intimate parts;</p><p>b. The accused forces the complaining witness to touch the accused&#39;s, the witness&#39;s own, or another person&#39;s intimate parts or material directly covering such intimate parts;</p><p>c. If the complaining witness is under the age of 13, the accused causes or assists the complaining witness to touch the accused&#39;s, the witness&#39;s own, or another person&#39;s intimate parts or material directly covering such intimate parts; or</p><p>d. The accused forces another person to touch the complaining witness&#39;s intimate parts or material directly covering such intimate parts.</p><p>1981, c. 397; 1987, c. 277; 1993, c. 549; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0568" target="_blank" rel="noreferrer">568</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0741" target="_blank" rel="noreferrer">741</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0645" target="_blank" rel="noreferrer">645</a>.</p>',8),s=[o];function r(c,l,h,u,p,d){return t(),n("div",null,s)}const g=e(i,[["render",r]]);export{f as __pageData,g as default};
