import{_ as e,o,c as n,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 18.2-67.5:2 Punishment upon conviction of certain subsequent felony sexual assault.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-67.5:2.md","filePath":"vacode/18.2-67.5:2.md"}'),i={name:"vacode/18.2-67.5:2.md"},a=t('<h1 id="§-18-2-67-5-2-punishment-upon-conviction-of-certain-subsequent-felony-sexual-assault" tabindex="-1">§ 18.2-67.5:2 Punishment upon conviction of certain subsequent felony sexual assault. <a class="header-anchor" href="#§-18-2-67-5-2-punishment-upon-conviction-of-certain-subsequent-felony-sexual-assault" aria-label="Permalink to &quot;§ 18.2-67.5:2 Punishment upon conviction of certain subsequent felony sexual assault.&quot;">​</a></h1><p>A. Any person convicted of (i) more than one offense specified in subsection B or (ii) one of the offenses specified in subsection B of this section and one of the offenses specified in subsection B of § <a href="/vacode/18.2-67.5:3/">18.2-67.5:3</a> when such offenses were not part of a common act, transaction, or scheme and who has been at liberty as defined in § <a href="/vacode/53.1-151/">53.1-151</a> between each conviction shall, upon conviction of the second or subsequent such offense, be sentenced to the maximum term authorized by statute for such offense and shall not have all or any part of such sentence suspended, provided that it is admitted, or found by the jury or judge before whom the person is tried, that he has been previously convicted of at least one of the specified offenses.</p><p>B. The provisions of subsection A shall apply to felony convictions for:</p><ol><li><p>Carnal knowledge of a child between 13 and 15 years of age in violation of § <a href="/vacode/18.2-63/">18.2-63</a> when the offense is committed by a person over the age of 18;</p></li><li><p>Carnal knowledge of certain minors in violation of § <a href="/vacode/18.2-64.1/">18.2-64.1</a>;</p></li><li><p>Aggravated sexual battery in violation of § <a href="/vacode/18.2-67.3/">18.2-67.3</a>;</p></li><li><p>Crimes against nature in violation of subsection B of § <a href="/vacode/18.2-361/">18.2-361</a>;</p></li><li><p>Sexual intercourse with one&#39;s own child or grandchild in violation of § <a href="/vacode/18.2-366/">18.2-366</a>;</p></li><li><p>Taking indecent liberties with a child in violation of § <a href="/vacode/18.2-370/">18.2-370</a> or <a href="/vacode/18.2-370.1/">18.2-370.1</a>; or</p></li><li><p>Conspiracy to commit any offense listed in subdivisions 1 through 6 pursuant to § <a href="/vacode/18.2-22/">18.2-22</a>.</p></li></ol><p>C. For purposes of this section, prior convictions shall include (i) adult convictions for felonies under the laws of any state or the United States that are substantially similar to those listed in subsection B and (ii) findings of not innocent, adjudications, or convictions in the case of a juvenile if the juvenile offense is substantially similar to those listed in subsection B, the offense would be a felony if committed by an adult in the Commonwealth, and the offense was committed less than 20 years before the second offense.</p><p>The Commonwealth shall notify the defendant in writing, at least 30 days prior to trial, of its intention to seek punishment pursuant to this section.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0333" target="_blank" rel="noreferrer">333</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0122" target="_blank" rel="noreferrer">122</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0900" target="_blank" rel="noreferrer">900</a>.</p>',7),s=[a];function r(l,c,f,h,u,d){return o(),n("div",null,s)}const m=e(i,[["render",r]]);export{v as __pageData,m as default};
