import{_ as e,o as t,c as n,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 18.2-67.5:3 Punishment upon conviction of certain subsequent violent felony sexual assault.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-67.5:3.md","filePath":"vacode/18.2-67.5:3.md"}'),i={name:"vacode/18.2-67.5:3.md"},a=o('<h1 id="§-18-2-67-5-3-punishment-upon-conviction-of-certain-subsequent-violent-felony-sexual-assault" tabindex="-1">§ 18.2-67.5:3 Punishment upon conviction of certain subsequent violent felony sexual assault. <a class="header-anchor" href="#§-18-2-67-5-3-punishment-upon-conviction-of-certain-subsequent-violent-felony-sexual-assault" aria-label="Permalink to &quot;§ 18.2-67.5:3 Punishment upon conviction of certain subsequent violent felony sexual assault.&quot;">​</a></h1><p>A. Any person convicted of more than one offense specified in subsection B, when such offenses were not part of a common act, transaction or scheme, and who has been at liberty as defined in § <a href="http://law.lis.virginia.gov/vacode/53.1-151/" target="_blank" rel="noreferrer">53.1-151</a> between each conviction shall, upon conviction of the second or subsequent such offense, be sentenced to life imprisonment and shall not have all or any portion of the sentence suspended, provided it is admitted, or found by the jury or judge before whom he is tried, that he has been previously convicted of at least one of the specified offenses.</p><p>B. The provisions of subsection A shall apply to convictions for:</p><ol><li><p>Rape in violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-61/" target="_blank" rel="noreferrer">18.2-61</a>;</p></li><li><p>Forcible sodomy in violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-67.1/" target="_blank" rel="noreferrer">18.2-67.1</a>;</p></li><li><p>Object sexual penetration in violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-67.2/" target="_blank" rel="noreferrer">18.2-67.2</a>;</p></li><li><p>Abduction with intent to defile in violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-48/" target="_blank" rel="noreferrer">18.2-48</a>; or</p></li><li><p>Conspiracy to commit any offense listed in subdivisions 1 through 4 pursuant to § <a href="http://law.lis.virginia.gov/vacode/18.2-22/" target="_blank" rel="noreferrer">18.2-22</a>.</p></li></ol><p>C. For purposes of this section, prior convictions shall include (i) adult convictions for felonies under the laws of any state or the United States that are substantially similar to those listed in subsection B and (ii) findings of not innocent, adjudications or convictions in the case of a juvenile if the juvenile offense is substantially similar to those listed in subsection B, the offense would be a felony if committed by an adult in the Commonwealth and the offense was committed less than twenty years before the second offense.</p><p>The Commonwealth shall notify the defendant in the indictment, information, or warrant, at least thirty days prior to trial, of its intention to seek punishment pursuant to this section.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0506" target="_blank" rel="noreferrer">506</a>.</p>',7),s=[a];function r(l,c,f,h,u,p){return t(),n("div",null,s)}const m=e(i,[["render",r]]);export{v as __pageData,m as default};
