import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 18.2-67.8 Closed preliminary hearings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-67.8.md","filePath":"vacode/18.2-67.8.md"}'),t={name:"vacode/18.2-67.8.md"},n=o('<h1 id="§-18-2-67-8-closed-preliminary-hearings" tabindex="-1">§ 18.2-67.8 Closed preliminary hearings. <a class="header-anchor" href="#§-18-2-67-8-closed-preliminary-hearings" aria-label="Permalink to &quot;§ 18.2-67.8 Closed preliminary hearings.&quot;">​</a></h1><p>In preliminary hearings for offenses charged under this article or under §§ <a href="http://law.lis.virginia.gov/vacode/18.2-361/" target="_blank" rel="noreferrer">18.2-361</a>, <a href="http://law.lis.virginia.gov/vacode/18.2-366/" target="_blank" rel="noreferrer">18.2-366</a>, <a href="http://law.lis.virginia.gov/vacode/18.2-370/" target="_blank" rel="noreferrer">18.2-370</a> or § <a href="http://law.lis.virginia.gov/vacode/18.2-370.1/" target="_blank" rel="noreferrer">18.2-370.1</a>, the court may, on its own motion or at the request of the Commonwealth, the complaining witness, the accused, or their counsel, exclude from the courtroom all persons except officers of the court and persons whose presence, in the judgment of the court, would be supportive of the complaining witness or the accused and would not impair the conduct of a fair hearing.</p><p>1981, c. 397; 1993, c. 440.</p>',3),i=[n];function s(c,l,h,d,p,_){return r(),a("div",null,i)}const g=e(t,[["render",s]]);export{m as __pageData,g as default};
