import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 18.2-73 When abortion lawful during second trimester of pregnancy.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-73.md","filePath":"vacode/18.2-73.md"}'),n={name:"vacode/18.2-73.md"},o=t('<h1 id="§-18-2-73-when-abortion-lawful-during-second-trimester-of-pregnancy" tabindex="-1">§ 18.2-73 When abortion lawful during second trimester of pregnancy. <a class="header-anchor" href="#§-18-2-73-when-abortion-lawful-during-second-trimester-of-pregnancy" aria-label="Permalink to &quot;§ 18.2-73 When abortion lawful during second trimester of pregnancy.&quot;">​</a></h1><p>Notwithstanding any of the provisions of § <a href="http://law.lis.virginia.gov/vacode/18.2-71/" target="_blank" rel="noreferrer">18.2-71</a> and in addition to the provisions of § <a href="http://law.lis.virginia.gov/vacode/18.2-72/" target="_blank" rel="noreferrer">18.2-72</a>, it shall be lawful for any physician licensed by the Board of Medicine to practice medicine and surgery, to terminate or attempt to terminate a human pregnancy or aid or assist in the termination of a human pregnancy by performing an abortion or causing a miscarriage on any woman during the second trimester of pregnancy and prior to the third trimester of pregnancy provided such procedure is performed in a hospital licensed by the State Department of Health or operated by the Department of Behavioral Health and Developmental Services.</p><p>1975, cc. 14, 15; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>.</p>',3),i=[o];function c(s,l,d,p,h,f){return r(),a("div",null,i)}const m=e(n,[["render",c]]);export{_ as __pageData,m as default};
