import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-74 When abortion or termination of pregnancy lawful after second trimester of pregnancy.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-74.md","filePath":"vacode/18.2-74.md"}'),n={name:"vacode/18.2-74.md"},o=r('<h1 id="§-18-2-74-when-abortion-or-termination-of-pregnancy-lawful-after-second-trimester-of-pregnancy" tabindex="-1">§ 18.2-74 When abortion or termination of pregnancy lawful after second trimester of pregnancy. <a class="header-anchor" href="#§-18-2-74-when-abortion-or-termination-of-pregnancy-lawful-after-second-trimester-of-pregnancy" aria-label="Permalink to &quot;§ 18.2-74 When abortion or termination of pregnancy lawful after second trimester of pregnancy.&quot;">​</a></h1><p>Notwithstanding any of the provisions of § <a href="http://law.lis.virginia.gov/vacode/18.2-71/" target="_blank" rel="noreferrer">18.2-71</a> and in addition to the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/18.2-72/" target="_blank" rel="noreferrer">18.2-72</a> and <a href="http://law.lis.virginia.gov/vacode/18.2-73/" target="_blank" rel="noreferrer">18.2-73</a>, it shall be lawful for any physician licensed by the Board of Medicine to practice medicine and surgery to terminate or attempt to terminate a human pregnancy or aid or assist in the termination of a human pregnancy by performing an abortion or causing a miscarriage on any woman in a stage of pregnancy subsequent to the second trimester provided the following conditions are met:</p><p>(a) Said operation is performed in a hospital licensed by the Virginia State Department of Health or operated by the Department of Behavioral Health and Developmental Services.</p><p>(b) The physician and two consulting physicians certify and so enter in the hospital record of the woman, that in their medical opinion, based upon their best clinical judgment, the continuation of the pregnancy is likely to result in the death of the woman or substantially and irremediably impair the mental or physical health of the woman.</p><p>(c) Measures for life support for the product of such abortion or miscarriage must be available and utilized if there is any clearly visible evidence of viability.</p><p>1975, cc. 14, 15; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>.</p>',6),i=[o];function s(l,c,p,f,h,d){return a(),t("div",null,i)}const _=e(n,[["render",s]]);export{g as __pageData,_ as default};
