import{_ as e,o as a,c as o,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 18.2-75 Conscience clause.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-75.md","filePath":"vacode/18.2-75.md"}'),r={name:"vacode/18.2-75.md"},t=i('<h1 id="§-18-2-75-conscience-clause" tabindex="-1">§ 18.2-75 Conscience clause. <a class="header-anchor" href="#§-18-2-75-conscience-clause" aria-label="Permalink to &quot;§ 18.2-75 Conscience clause.&quot;">​</a></h1><p>Nothing in §§ <a href="http://law.lis.virginia.gov/vacode/18.2-72/" target="_blank" rel="noreferrer">18.2-72</a>, <a href="http://law.lis.virginia.gov/vacode/18.2-73/" target="_blank" rel="noreferrer">18.2-73</a> or § <a href="http://law.lis.virginia.gov/vacode/18.2-74/" target="_blank" rel="noreferrer">18.2-74</a> shall require a hospital or other medical facility or physician to admit any patient under the provisions hereof for the purpose of performing an abortion. In addition, any person who shall state in writing an objection to any abortion or all abortions on personal, ethical, moral or religious grounds shall not be required to participate in procedures which will result in such abortion, and the refusal of such person, hospital or other medical facility to participate therein shall not form the basis of any claim for damages on account of such refusal or for any disciplinary or recriminatory action against such person, nor shall any such person be denied employment because of such objection or refusal. The written objection shall remain in effect until such person shall revoke it in writing or terminate his association with the facility with which it is filed.</p><p>Code 1950, § 18.1-63.1; 1974, c. 679; 1975, cc. 14, 15.</p>',3),n=[t];function s(c,l,h,p,d,f){return a(),o("div",null,n)}const m=e(r,[["render",s]]);export{_ as __pageData,m as default};
