import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-76 Informed written consent required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-76.md","filePath":"vacode/18.2-76.md"}'),a={name:"vacode/18.2-76.md"},o=i('<h1 id="§-18-2-76-informed-written-consent-required" tabindex="-1">§ 18.2-76 Informed written consent required. <a class="header-anchor" href="#§-18-2-76-informed-written-consent-required" aria-label="Permalink to &quot;§ 18.2-76 Informed written consent required.&quot;">​</a></h1><p>Before performing any abortion or inducing any miscarriage or terminating a pregnancy as provided in § <a href="/vacode/18.2-72/">18.2-72</a>, <a href="/vacode/18.2-73/">18.2-73</a>, or <a href="/vacode/18.2-74/">18.2-74</a>, the physician or, if such abortion, induction, or termination is to be performed pursuant to § <a href="/vacode/18.2-72/">18.2-72</a>, either the physician or the nurse practitioner authorized pursuant to clause (ii) of § <a href="/vacode/18.2-72/">18.2-72</a> to perform such abortion, induction, or termination shall obtain the informed written consent of the pregnant woman. However, if the woman has been adjudicated incapacitated by any court of competent jurisdiction or if the physician or, if the abortion, induction, or termination is to be performed pursuant to § <a href="/vacode/18.2-72/">18.2-72</a>, either the physician or the nurse practitioner authorized pursuant to clause (ii) of § <a href="/vacode/18.2-72/">18.2-72</a> to perform such abortion, induction, or termination knows or has good reason to believe that such woman is incapacitated as adjudicated by a court of competent jurisdiction, then only after permission is given in writing by a parent, guardian, committee, or other person standing in loco parentis to the woman, may the physician or, if the abortion, induction, or termination is to be performed pursuant to § <a href="/vacode/18.2-72/">18.2-72</a>, either the physician or the nurse practitioner authorized pursuant to clause (ii) of § <a href="/vacode/18.2-72/">18.2-72</a> to perform such abortion, induction, or termination perform the abortion or otherwise terminate the pregnancy.</p><p>Code 1950, § 18.1-62.1; 1970, c. 508; 1972, c. 823; 1975, cc. 14, 15; 1979, c. 250; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0801" target="_blank" rel="noreferrer">801</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0473" target="_blank" rel="noreferrer">473</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0784" target="_blank" rel="noreferrer">784</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0131" target="_blank" rel="noreferrer">131</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0898" target="_blank" rel="noreferrer">898</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0899" target="_blank" rel="noreferrer">899</a>.</p>',3),n=[o];function c(s,h,d,f,p,u){return r(),t("div",null,n)}const m=e(a,[["render",c]]);export{g as __pageData,m as default};
