import{_ as e,o,c as r,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 18.2-77 Burning or destroying dwelling house, etc.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-77.md","filePath":"vacode/18.2-77.md"}'),a={name:"vacode/18.2-77.md"},n=i('<h1 id="§-18-2-77-burning-or-destroying-dwelling-house-etc" tabindex="-1">§ 18.2-77 Burning or destroying dwelling house, etc. <a class="header-anchor" href="#§-18-2-77-burning-or-destroying-dwelling-house-etc" aria-label="Permalink to &quot;§ 18.2-77 Burning or destroying dwelling house, etc.&quot;">​</a></h1><p>A. If any person maliciously (i) burns, or by use of any explosive device or substance destroys, in whole or in part, or causes to be burned or destroyed, or (ii) aids, counsels or procures the burning or destruction of any dwelling house or manufactured home whether belonging to himself or another, or any occupied hotel, hospital, mental health facility, or other house in which persons usually dwell or lodge, any occupied railroad car, boat, vessel, or river craft in which persons usually dwell or lodge, or any occupied jail or prison, or any occupied church or occupied building owned or leased by a church that is immediately adjacent to a church, he shall be guilty of a felony, punishable by imprisonment for life or for any period not less than five years and, subject to subdivision g of § <a href="http://law.lis.virginia.gov/vacode/18.2-10/" target="_blank" rel="noreferrer">18.2-10</a>, a fine of not more than $100,000. Any person who maliciously sets fire to anything, or aids, counsels or procures the setting fire to anything, by the burning whereof such occupied dwelling house, manufactured home, hotel, hospital, mental health facility or other house, or railroad car, boat, vessel, or river craft, jail or prison, church or building owned or leased by a church that is immediately adjacent to a church, is burned shall be guilty of a violation of this subsection.</p><p>B. Any such burning or destruction when the building or other place mentioned in subsection A is unoccupied, shall be punishable as a Class 4 felony.</p><p>C. For purposes of this section, &quot;church&quot; shall be defined as in § <a href="http://law.lis.virginia.gov/vacode/18.2-127/" target="_blank" rel="noreferrer">18.2-127</a>.</p><p>Code 1950, § 18.1-75; 1960, c. 358; 1975, cc. 14, 15; 1977, c. 63; 1978, c. 443; 1993, c. 406; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0832" target="_blank" rel="noreferrer">832</a>.</p>',5),t=[n];function s(l,c,h,u,d,p){return o(),r("div",null,t)}const b=e(a,[["render",s]]);export{f as __pageData,b as default};
