import{_ as e,o as r,c as n,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 18.2-81 Burning or destroying personal property, standing grain, etc.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-81.md","filePath":"vacode/18.2-81.md"}'),t={name:"vacode/18.2-81.md"},a=o('<h1 id="§-18-2-81-burning-or-destroying-personal-property-standing-grain-etc" tabindex="-1">§ 18.2-81 Burning or destroying personal property, standing grain, etc. <a class="header-anchor" href="#§-18-2-81-burning-or-destroying-personal-property-standing-grain-etc" aria-label="Permalink to &quot;§ 18.2-81 Burning or destroying personal property, standing grain, etc.&quot;">​</a></h1><p>If any person maliciously, or with intent to defraud an insurance company or other person, set fire to or burn or destroy by any explosive device or substance, or cause to be burned, or destroyed by any explosive device or substance, or aid, counsel, or procure the burning or destroying by any explosive device or substance, of any personal property, standing grain or other crop, he shall, if the thing burnt or destroyed be of the value of $1,000 or more, be guilty of a Class 4 felony; and if the thing burnt or destroyed be of less value, he shall be guilty of a Class 1 misdemeanor.</p><p>Code 1950, §§ 18.1-79, 18.1-85; 1960, c. 358; 1972, c. 53; 1975, cc. 14, 15; 1981, c. 197; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0764" target="_blank" rel="noreferrer">764</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0765" target="_blank" rel="noreferrer">765</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0089" target="_blank" rel="noreferrer">89</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0401" target="_blank" rel="noreferrer">401</a>.</p>',3),i=[a];function s(l,c,g,p,d,u){return r(),n("div",null,i)}const h=e(t,[["render",s]]);export{_ as __pageData,h as default}