import{_ as i,o as t,c as e,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 19.2-11.02 Prohibiting inquiry into the immigration status of certain victims or witnesses of crime.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-11.02.md","filePath":"vacode/19.2-11.02.md"}'),r={name:"vacode/19.2-11.02.md"},n=o('<h1 id="§-19-2-11-02-prohibiting-inquiry-into-the-immigration-status-of-certain-victims-or-witnesses-of-crime" tabindex="-1">§ 19.2-11.02 Prohibiting inquiry into the immigration status of certain victims or witnesses of crime. <a class="header-anchor" href="#§-19-2-11-02-prohibiting-inquiry-into-the-immigration-status-of-certain-victims-or-witnesses-of-crime" aria-label="Permalink to &quot;§ 19.2-11.02 Prohibiting inquiry into the immigration status of certain victims or witnesses of crime.&quot;">​</a></h1><p>A. No law-enforcement officer, as defined in § <a href="/vacode/9.1-101/">9.1-101</a>, shall, in connection with the report, investigation, or prosecution of a criminal violation of state or local law, inquire into the immigration status of any person who (i) reports that he is a victim of the crime or is the parent or guardian of a minor victim of the crime or (ii) is a witness in the investigation of the crime or the parent or guardian of a minor witness to the crime.</p><p>B. Nothing in this section shall prohibit a law-enforcement officer from inquiring into the immigration status of the parent or guardian of a minor victim if such parent or guardian has been arrested for, has been charged with, or is being investigated for a crime against the minor victim.</p><p>C. Nothing in this section shall affect the enforcement or implementation of § <a href="/vacode/18.2-59/">18.2-59</a>, subdivision 10 of § <a href="/vacode/18.2-308.09/">18.2-308.09</a>, or subdivision B 1 of § <a href="/vacode/18.2-308.2:2/">18.2-308.2:2</a>, or prohibit a law-enforcement officer from inquiring into a person&#39;s immigration status to enforce or implement such sections.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0273" target="_blank" rel="noreferrer">273</a>.</p>',5),a=[n];function s(c,m,h,f,p,d){return t(),e("div",null,a)}const u=i(r,[["render",s]]);export{g as __pageData,u as default};
