import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse(`{"title":"§ 19.2-11.2 Crime victim's right to nondisclosure of certain information; exceptions; testimonial privilege.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-11.2.md","filePath":"vacode/19.2-11.2.md"}`),o={name:"vacode/19.2-11.2.md"},a=r('<h1 id="§-19-2-11-2-crime-victim-s-right-to-nondisclosure-of-certain-information-exceptions-testimonial-privilege" tabindex="-1">§ 19.2-11.2 Crime victim&#39;s right to nondisclosure of certain information; exceptions; testimonial privilege. <a class="header-anchor" href="#§-19-2-11-2-crime-victim-s-right-to-nondisclosure-of-certain-information-exceptions-testimonial-privilege" aria-label="Permalink to &quot;§ 19.2-11.2 Crime victim&#39;s right to nondisclosure of certain information; exceptions; testimonial privilege.&quot;">​</a></h1><p>Upon request of any witness in a criminal prosecution under § <a href="/vacode/18.2-46.2/">18.2-46.2</a>, <a href="/vacode/18.2-46.3/">18.2-46.3</a>, or <a href="/vacode/18.2-248/">18.2-248</a> or of any violent felony as defined by subsection C of § <a href="/vacode/17.1-805/">17.1-805</a>, or any crime victim, neither a law-enforcement agency, the attorney for the Commonwealth, the counsel for a defendant, a court nor the Department of Corrections, nor any employee of any of them, may disclose, except among themselves, the residential address, any telephone number, email address, or place of employment of the witness or victim or a member of the witness&#39; or victim&#39;s family, except to the extent that disclosure is (i) of the site of the crime, (ii) required by law or Rules of the Supreme Court, (iii) necessary for law-enforcement purposes or preparation for court proceedings, or (iv) permitted by the court for good cause.</p><p>Except with the written consent of the victim of any crime involving any sexual assault, sexual abuse, or family abuse or the victim&#39;s next of kin if the victim is a minor and the victim&#39;s death results from any crime, a law-enforcement agency may not disclose to the public information that directly or indirectly identifies the victim of such crime except to the extent that disclosure is (a) of the site of the crime, (b) required by law, (c) necessary for law-enforcement purposes, or (d) permitted by the court for good cause. In addition, at the request of the victim to the Court of Appeals of Virginia or the Supreme Court of Virginia hearing, on or after July 1, 2007, the case of a crime involving any sexual assault or sexual abuse, no appellate decision shall contain the first or last name of the victim.</p><p>Nothing herein shall limit the right to examine witnesses in a court of law or otherwise affect the conduct of any criminal proceeding.</p><p>1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0845" target="_blank" rel="noreferrer">845</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0931" target="_blank" rel="noreferrer">931</a>; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0810" target="_blank" rel="noreferrer">810</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0818" target="_blank" rel="noreferrer">818</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0764" target="_blank" rel="noreferrer">764</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0744" target="_blank" rel="noreferrer">744</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0500" target="_blank" rel="noreferrer">500</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0047" target="_blank" rel="noreferrer">47</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0083" target="_blank" rel="noreferrer">83</a>.</p>',5),n=[a];function s(c,l,f,h,g,m){return i(),t("div",null,n)}const d=e(o,[["render",s]]);export{u as __pageData,d as default};
