import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 19.2-11.3 Virginia Crime Victim-Witness Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-11.3.md","filePath":"vacode/19.2-11.3.md"}'),i={name:"vacode/19.2-11.3.md"},n=a('<h1 id="§-19-2-11-3-virginia-crime-victim-witness-fund" tabindex="-1">§ 19.2-11.3 Virginia Crime Victim-Witness Fund. <a class="header-anchor" href="#§-19-2-11-3-virginia-crime-victim-witness-fund" aria-label="Permalink to &quot;§ 19.2-11.3 Virginia Crime Victim-Witness Fund.&quot;">​</a></h1><p>There is hereby established the Virginia Crime Victim-Witness Fund as a special nonreverting fund to be administered by the Department of Criminal Justice Services to support victim and witness services that meet the minimum standards prescribed for such programs under § <a href="http://law.lis.virginia.gov/vacode/19.2-11.1/" target="_blank" rel="noreferrer">19.2-11.1</a>. A portion of the sum collected pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/16.1-69.48:1/" target="_blank" rel="noreferrer">16.1-69.48:1</a>, <a href="http://law.lis.virginia.gov/vacode/17.1-275.1/" target="_blank" rel="noreferrer">17.1-275.1</a>, <a href="http://law.lis.virginia.gov/vacode/17.1-275.2/" target="_blank" rel="noreferrer">17.1-275.2</a>, <a href="http://law.lis.virginia.gov/vacode/17.1-275.3/" target="_blank" rel="noreferrer">17.1-275.3</a>, <a href="http://law.lis.virginia.gov/vacode/17.1-275.4/" target="_blank" rel="noreferrer">17.1-275.4</a>, <a href="http://law.lis.virginia.gov/vacode/17.1-275.7/" target="_blank" rel="noreferrer">17.1-275.7</a>, <a href="http://law.lis.virginia.gov/vacode/17.1-275.8/" target="_blank" rel="noreferrer">17.1-275.8</a>, and <a href="http://law.lis.virginia.gov/vacode/17.1-275.9/" target="_blank" rel="noreferrer">17.1-275.9</a>, as specified in these sections, shall be deposited into the state treasury to the credit of this Fund. The Fund shall be distributed according to grant procedures adopted pursuant to § <a href="http://law.lis.virginia.gov/vacode/9.1-104/" target="_blank" rel="noreferrer">9.1-104</a> and shall be established on the books of the Comptroller. Any funds remaining in such Fund at the end of the biennium shall not revert to the general fund, but shall remain in the Fund. Interest earned on the Fund shall be credited to the Fund.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0371" target="_blank" rel="noreferrer">371</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0831" target="_blank" rel="noreferrer">831</a>.</p>',3),o=[n];function s(l,d,h,c,g,v){return r(),t("div",null,o)}const _=e(i,[["render",s]]);export{p as __pageData,_ as default};
