import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 19.2-152.10:1 Hope Card Program for persons protected by protective orders.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-152.10:1.md","filePath":"vacode/19.2-152.10:1.md"}'),a={name:"vacode/19.2-152.10:1.md"},s=o('<h1 id="§-19-2-152-10-1-hope-card-program-for-persons-protected-by-protective-orders" tabindex="-1">§ 19.2-152.10:1 Hope Card Program for persons protected by protective orders. <a class="header-anchor" href="#§-19-2-152-10-1-hope-card-program-for-persons-protected-by-protective-orders" aria-label="Permalink to &quot;§ 19.2-152.10:1 Hope Card Program for persons protected by protective orders.&quot;">​</a></h1><p>The Office of the Executive Secretary of the Supreme Court of Virginia shall develop and all district courts and circuit courts shall implement the Hope Card Program (the Program) for the issuance of a Hope Card to any person who has been issued a protective order pursuant to § <a href="/vacode/19.2-152.10/">19.2-152.10</a> or <a href="/vacode/16.1-279.1/">16.1-279.1</a> by any district court or circuit court. A Hope Card issued pursuant to the Program shall be a durable, plastic, wallet-sized card containing, to the extent possible, essential information about the protective order, such as the identifying information and characteristics of the person subject to the protective order, the issuance and expiration date of the protective order, the terms of the protective order, and the names of any other persons protected by the protective order.</p><p>2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0374" target="_blank" rel="noreferrer">374</a>.</p>',3),c=[s];function i(d,n,p,h,l,f){return r(),t("div",null,c)}const m=e(a,[["render",i]]);export{u as __pageData,m as default};
