import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 19.2-152.15 Return or disposal of firearms.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-152.15.md","filePath":"vacode/19.2-152.15.md"}'),t={name:"vacode/19.2-152.15.md"},n=o('<h1 id="§-19-2-152-15-return-or-disposal-of-firearms" tabindex="-1">§ 19.2-152.15 Return or disposal of firearms. <a class="header-anchor" href="#§-19-2-152-15-return-or-disposal-of-firearms" aria-label="Permalink to &quot;§ 19.2-152.15 Return or disposal of firearms.&quot;">​</a></h1><p>A. Any firearm taken into custody pursuant to § <a href="/vacode/19.2-152.13/">19.2-152.13</a> or <a href="/vacode/19.2-152.14/">19.2-152.14</a> and held by a law-enforcement agency shall be returned by such agency to the person from whom the firearm was taken upon a court order for the return of the firearm issued pursuant to § <a href="/vacode/19.2-152.14/">19.2-152.14</a> or the expiration or dissolution of an order issued pursuant to § <a href="/vacode/19.2-152.13/">19.2-152.13</a> or <a href="/vacode/19.2-152.14/">19.2-152.14</a>. Such agency shall return the firearm within five days of receiving a written request for the return of the firearm by the person from whom the firearm was taken and a copy of the receipt provided to such person pursuant to § <a href="/vacode/19.2-152.13/">19.2-152.13</a>. Prior to returning the firearm to such person, the law-enforcement agency holding the firearm shall confirm that such person is no longer subject to an order issued pursuant to § <a href="/vacode/19.2-152.13/">19.2-152.13</a> or <a href="/vacode/19.2-152.14/">19.2-152.14</a> and is not otherwise prohibited by law from possessing a firearm.</p><p>B. A firearm taken into custody pursuant to pursuant to § <a href="/vacode/19.2-152.13/">19.2-152.13</a> or <a href="/vacode/19.2-152.14/">19.2-152.14</a> and held by a law-enforcement agency may be disposed of in accordance with the provisions of § <a href="/vacode/15.2-1721/">15.2-1721</a> if (i) the person from whom the firearm was taken provides written authorization for such disposal to the agency or (ii) the firearm remains in the possession of the agency more than 120 days after such person is no longer subject to an order issued pursuant to § <a href="/vacode/19.2-152.13/">19.2-152.13</a> or <a href="/vacode/19.2-152.14/">19.2-152.14</a> and such person has not submitted a request in writing for the return of the firearm.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0887" target="_blank" rel="noreferrer">887</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0888" target="_blank" rel="noreferrer">888</a>.</p>',4),s=[n];function i(h,f,c,d,u,p){return r(),a("div",null,s)}const _=e(t,[["render",i]]);export{m as __pageData,_ as default};
