import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 19.2-152.17 Immunity of law-enforcement officers, etc.; chapter not exclusive.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-152.17.md","filePath":"vacode/19.2-152.17.md"}'),o={name:"vacode/19.2-152.17.md"},i=a('<h1 id="§-19-2-152-17-immunity-of-law-enforcement-officers-etc-chapter-not-exclusive" tabindex="-1">§ 19.2-152.17 Immunity of law-enforcement officers, etc.; chapter not exclusive. <a class="header-anchor" href="#§-19-2-152-17-immunity-of-law-enforcement-officers-etc-chapter-not-exclusive" aria-label="Permalink to &quot;§ 19.2-152.17 Immunity of law-enforcement officers, etc.; chapter not exclusive.&quot;">​</a></h1><p>A. An attorney for the Commonwealth or a law-enforcement officer shall be immune from civil liability for any act or omission related to petitioning or declining to petition for a substantial risk order pursuant to this chapter.</p><p>B. Any law-enforcement agency or law-enforcement officer that takes into custody, stores, possesses, or transports a firearm pursuant to § <a href="/vacode/19.2-152.13/">19.2-152.13</a> or <a href="/vacode/19.2-152.14/">19.2-152.14</a>, or by a search warrant for a person who has failed to voluntarily relinquish his firearm, shall be immune from civil or criminal liability for any damage to or deterioration, loss, or theft of such firearm.</p><p>C. Nothing in this chapter precludes a law-enforcement officer from conducting a search for a firearm or removing a firearm from a person under any other lawful authority.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0887" target="_blank" rel="noreferrer">887</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0888" target="_blank" rel="noreferrer">888</a>.</p>',5),n=[i];function c(s,f,l,m,h,p){return r(),t("div",null,n)}const d=e(o,[["render",c]]);export{_ as __pageData,d as default};
