import{_ as e,o as r,c as a,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 19.2-152.2 Purpose; establishment of pretrial services and services agencies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-152.2.md","filePath":"vacode/19.2-152.2.md"}'),t={name:"vacode/19.2-152.2.md"},s=i('<h1 id="§-19-2-152-2-purpose-establishment-of-pretrial-services-and-services-agencies" tabindex="-1">§ 19.2-152.2 Purpose; establishment of pretrial services and services agencies. <a class="header-anchor" href="#§-19-2-152-2-purpose-establishment-of-pretrial-services-and-services-agencies" aria-label="Permalink to &quot;§ 19.2-152.2 Purpose; establishment of pretrial services and services agencies.&quot;">​</a></h1><p>It is the purpose of this article to provide more effective protection of society by establishing pretrial services agencies that will assist judicial officers in discharging their duties pursuant to Article 1 (§ <a href="/vacode/19.2-119/">19.2-119</a> et seq.) of Chapter 9. Such agencies are intended to provide better information and services for use by judicial officers in determining the risk to public safety and the assurance of appearance of persons age 18 or over or persons under the age of 18 who have been transferred for trial as adults held in custody and charged with an offense, other than an offense punishable as a Class 1 felony, who are pending trial or hearing. Any city, county or combination thereof may establish a pretrial services agency and any city, county or combination thereof required to submit a community-based corrections plan pursuant to § <a href="/vacode/53.1-82.1/">53.1-82.1</a> shall establish a pretrial services agency.</p><p>1994, 2nd Sp. Sess., cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0001" target="_blank" rel="noreferrer">1</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0829" target="_blank" rel="noreferrer">829</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0846" target="_blank" rel="noreferrer">846</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0378" target="_blank" rel="noreferrer">378</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0133" target="_blank" rel="noreferrer">133</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0344" target="_blank" rel="noreferrer">344</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0345" target="_blank" rel="noreferrer">345</a>.</p>',3),n=[s];function o(c,l,f,p,h,g){return r(),a("div",null,n)}const u=e(t,[["render",o]]);export{v as __pageData,u as default};
