import{_ as e,o as t,c as n,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 19.2-163.01:1 Supplementing compensation of public defender.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-163.01:1.md","filePath":"vacode/19.2-163.01:1.md"}'),i={name:"vacode/19.2-163.01:1.md"},a=o('<h1 id="§-19-2-163-01-1-supplementing-compensation-of-public-defender" tabindex="-1">§ 19.2-163.01:1 Supplementing compensation of public defender. <a class="header-anchor" href="#§-19-2-163-01-1-supplementing-compensation-of-public-defender" aria-label="Permalink to &quot;§ 19.2-163.01:1 Supplementing compensation of public defender.&quot;">​</a></h1><p>A. The governing body of any county or city may supplement the compensation of the public defender or any of his deputies or employees above the compensation fixed by the executive director, in such amounts as it may deem expedient. Such additional compensation shall be wholly payable from the funds of any such county or city.</p><p>B. Due to the privileged and protected nature of the attorney-client relationship and the statutory scope of representation provided in §§ <a href="http://law.lis.virginia.gov/vacode/19.2-157/" target="_blank" rel="noreferrer">19.2-157</a> and <a href="http://law.lis.virginia.gov/vacode/19.2-163.3/" target="_blank" rel="noreferrer">19.2-163.3</a>, no county or city providing a supplement to compensation under this section shall place any condition or requirement upon the receipt of such funds.</p><p>C. Funds provided by any county or city under this section shall be paid directly to the employees with notice to the Indigent Defense Commission of any amount so provided.</p><p>2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0536" target="_blank" rel="noreferrer">536</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0815" target="_blank" rel="noreferrer">815</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0314" target="_blank" rel="noreferrer">314</a>.</p>',5),r=[a];function p(c,s,l,d,f,h){return t(),n("div",null,r)}const _=e(i,[["render",p]]);export{m as __pageData,_ as default};
