import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 19.2-169.8 Orders for evaluation or treatment; duties of clerk; copies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-169.8.md","filePath":"vacode/19.2-169.8.md"}'),a={name:"vacode/19.2-169.8.md"},i=t('<h1 id="§-19-2-169-8-orders-for-evaluation-or-treatment-duties-of-clerk-copies" tabindex="-1">§ 19.2-169.8 Orders for evaluation or treatment; duties of clerk; copies. <a class="header-anchor" href="#§-19-2-169-8-orders-for-evaluation-or-treatment-duties-of-clerk-copies" aria-label="Permalink to &quot;§ 19.2-169.8 Orders for evaluation or treatment; duties of clerk; copies.&quot;">​</a></h1><p>A. Whenever a court orders an evaluation pursuant to § <a href="/vacode/19.2-168.1/">19.2-168.1</a>, <a href="/vacode/19.2-169.1/">19.2-169.1</a>, or <a href="/vacode/19.2-169.5/">19.2-169.5</a> or orders treatment pursuant to § <a href="/vacode/19.2-169.2/">19.2-169.2</a> or <a href="/vacode/19.2-169.6/">19.2-169.6</a>, the clerk of the court shall provide a copy of the order to the appointed evaluator or to the director of the community services board, behavioral health authority, or hospital named in the order as soon as practicable but no later than the close of business on the next business day following entry of the order. The party requesting the evaluation pursuant to § <a href="/vacode/19.2-168.1/">19.2-168.1</a>, <a href="/vacode/19.2-169.1/">19.2-169.1</a>, or <a href="/vacode/19.2-169.5/">19.2-169.5</a>, the attorney for the Commonwealth if treatment is ordered pursuant to § <a href="/vacode/19.2-169.2/">19.2-169.2</a>, or the petitioner if treatment is ordered pursuant to § <a href="/vacode/19.2-169.6/">19.2-169.6</a> shall be responsible for providing to the court the name, address, and other contact information for the appointed evaluator or the director of the community services board, behavioral health authority, or hospital unless the court or clerk already has this information. The appointed evaluator or the director of the community services board, behavioral health authority, or hospital shall acknowledge receipt of the order to the clerk of the court on a form developed by the Office of the Executive Secretary of the Supreme Court of Virginia as soon as practicable but no later than the close of business on the next business day following receipt of the order. The clerk shall also provide a copy of the order to the Department of Behavioral Health and Developmental Services.</p><p>B. No person shall be liable for any act or omission relating to the performance of any requirement set forth in subsection A unless the person was grossly negligent or engaged in willful misconduct.</p><p>2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0446" target="_blank" rel="noreferrer">446</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0449" target="_blank" rel="noreferrer">449</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0074" target="_blank" rel="noreferrer">74</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0075" target="_blank" rel="noreferrer">75</a>.</p>',4),n=[i];function s(l,h,c,d,f,p){return r(),o("div",null,n)}const m=e(a,[["render",s]]);export{v as __pageData,m as default};
