import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 19.2-174.1 Information required prior to admission to a mental health facility.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-174.1.md","filePath":"vacode/19.2-174.1.md"}'),i={name:"vacode/19.2-174.1.md"},o=t('<h1 id="§-19-2-174-1-information-required-prior-to-admission-to-a-mental-health-facility" tabindex="-1">§ 19.2-174.1 Information required prior to admission to a mental health facility. <a class="header-anchor" href="#§-19-2-174-1-information-required-prior-to-admission-to-a-mental-health-facility" aria-label="Permalink to &quot;§ 19.2-174.1 Information required prior to admission to a mental health facility.&quot;">​</a></h1><p>Prior to any person being placed into the custody of the Commissioner for evaluation or treatment pursuant to §§ <a href="/vacode/19.2-169.2/">19.2-169.2</a>, <a href="/vacode/19.2-169.3/">19.2-169.3</a>, <a href="/vacode/19.2-169.6/">19.2-169.6</a>, <a href="/vacode/19.2-182.2/">19.2-182.2</a>, and <a href="/vacode/19.2-182.3/">19.2-182.3</a>, and Chapter 9 (§ <a href="/vacode/37.2-900/">37.2-900</a> et seq.) of Title 37.2, the court or special justice shall provide the Commissioner with the following, if available: (i) the commitment order, (ii) the names and addresses for the attorney for the Commonwealth, the attorney for the person and the judge holding jurisdiction over the person, (iii) a copy of the warrant or indictment, and (iv) a copy of the criminal incident information as defined in § <a href="/vacode/2.2-3706.1/">2.2-3706.1</a> or a copy of the arrest report or a summary of the facts relating to the crime. The party requesting the placement into the Commissioner&#39;s custody or, in the case of admissions pursuant to §§ <a href="/vacode/19.2-169.3/">19.2-169.3</a> and <a href="/vacode/19.2-169.6/">19.2-169.6</a>, and Chapter 9 (§ <a href="/vacode/37.2-900/">37.2-900</a> et seq.) of Title 37.2, the person having custody over the defendant or inmate shall gather the above information for submission to the court at the hearing. If the information is not available at the hearing, it shall be provided by the party requesting placement or the person having custody directly to the Commissioner within 96 hours of the person being placed into the Commissioner&#39;s custody. If the 96-hour period expires on a Saturday, Sunday or legal holiday, the 96 hours shall be extended to the next day that is not a Saturday, Sunday or legal holiday.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0645" target="_blank" rel="noreferrer">645</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0946" target="_blank" rel="noreferrer">946</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0985" target="_blank" rel="noreferrer">985</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0837" target="_blank" rel="noreferrer">837</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0989" target="_blank" rel="noreferrer">989</a>, cls. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0004" target="_blank" rel="noreferrer">4</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0005" target="_blank" rel="noreferrer">5</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1018" target="_blank" rel="noreferrer">1018</a>, cls. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0004" target="_blank" rel="noreferrer">4</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0005" target="_blank" rel="noreferrer">5</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1042" target="_blank" rel="noreferrer">1042</a>, cls. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0010" target="_blank" rel="noreferrer">10</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0011" target="_blank" rel="noreferrer">11</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0340" target="_blank" rel="noreferrer">340</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0406" target="_blank" rel="noreferrer">406</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0483" target="_blank" rel="noreferrer">483</a>.</p>',3),n=[o];function l(h,s,f,g,c,d){return r(),a("div",null,n)}const m=e(i,[["render",l]]);export{v as __pageData,m as default};
