import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 19.2-187.2 Procedure for subpoena duces tecum of analysis evidence.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-187.2.md","filePath":"vacode/19.2-187.2.md"}'),r={name:"vacode/19.2-187.2.md"},n=o('<h1 id="§-19-2-187-2-procedure-for-subpoena-duces-tecum-of-analysis-evidence" tabindex="-1">§ 19.2-187.2 Procedure for subpoena duces tecum of analysis evidence. <a class="header-anchor" href="#§-19-2-187-2-procedure-for-subpoena-duces-tecum-of-analysis-evidence" aria-label="Permalink to &quot;§ 19.2-187.2 Procedure for subpoena duces tecum of analysis evidence.&quot;">​</a></h1><p>No subpoena duces tecum shall issue for the production of writings or documents used to reach the conclusion contained in a certificate of analysis prepared pursuant to § <a href="http://law.lis.virginia.gov/vacode/19.2-187/" target="_blank" rel="noreferrer">19.2-187</a> except upon affidavit that the requested writings or documents are material. Upon a showing by the Commonwealth that the production of such writings and documents would place an undue burden on the Department of Forensic Science, the court may order that the subpoena duces tecum be satisfied by making the writings and documents available for inspection by the requesting party at the laboratory site where the analysis was performed or at the laboratory operated by the Department of Forensic Science which is closest to the court in which the case is pending.</p><p>1993, c. 629; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>.</p>',3),s=[n];function i(c,d,u,h,l,p){return t(),a("div",null,s)}const m=e(r,[["render",i]]);export{_ as __pageData,m as default};
