import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 19.2-188.1 Testimony regarding identification of controlled substances.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-188.1.md","filePath":"vacode/19.2-188.1.md"}'),i={name:"vacode/19.2-188.1.md"},n=r('<h1 id="§-19-2-188-1-testimony-regarding-identification-of-controlled-substances" tabindex="-1">§ 19.2-188.1 Testimony regarding identification of controlled substances. <a class="header-anchor" href="#§-19-2-188-1-testimony-regarding-identification-of-controlled-substances" aria-label="Permalink to &quot;§ 19.2-188.1 Testimony regarding identification of controlled substances.&quot;">​</a></h1><p>A. In any preliminary hearing on a violation of Chapter 11 (§ <a href="/vacode/4.1-1100/">4.1-1100</a> et seq.) of Title 4.1, Article 1 (§ <a href="/vacode/18.2-247/">18.2-247</a> et seq.) of Chapter 7 of Title 18.2, or subdivision 6 of § <a href="/vacode/53.1-203/">53.1-203</a>, any law-enforcement officer shall be permitted to testify as to the results of field tests that have been approved by the Department of Forensic Science pursuant to regulations adopted in accordance with the Administrative Process Act (§ <a href="/vacode/2.2-4000/">2.2-4000</a> et seq.), regarding whether or not any substance the identity of which is at issue in such hearing is a controlled substance, imitation controlled substance, or marijuana, as defined in § <a href="/vacode/18.2-247/">18.2-247</a>.</p><p>B. In any trial for a violation of § <a href="/vacode/4.1-1105.1/">4.1-1105.1</a>, any law-enforcement officer shall be permitted to testify as to the results of any marijuana field test approved as accurate and reliable by the Department of Forensic Science pursuant to regulations adopted in accordance with the Administrative Process Act (§ <a href="/vacode/2.2-4000/">2.2-4000</a> et seq.), regarding whether or not any plant material, the identity of which is at issue, is marijuana provided the defendant has been given written notice of his right to request a full chemical analysis. Such notice shall be on a form approved by the Supreme Court and shall be provided to the defendant prior to trial.</p><p>In any case in which the person accused of a violation of § <a href="/vacode/4.1-1105.1/">4.1-1105.1</a>, or the attorney of record for the accused, desires a full chemical analysis of the alleged plant material, he may, by motion prior to trial before the court in which the charge is pending, request such a chemical analysis. Upon such motion, the court shall order that the analysis be performed by the Department of Forensic Science in accordance with the provisions of § <a href="/vacode/18.2-247/">18.2-247</a> and shall prescribe in its order the method of custody, transfer, and return of evidence submitted for chemical analysis.</p><p>1991, c. 477; 1993, c. 33; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0447" target="_blank" rel="noreferrer">447</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0060" target="_blank" rel="noreferrer">60</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0831" target="_blank" rel="noreferrer">831</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0550" target="_blank" rel="noreferrer">550</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>.</p>',5),o=[n];function s(c,l,h,d,f,p){return a(),t("div",null,o)}const m=e(i,[["render",s]]);export{u as __pageData,m as default};
