import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 19.2-188.4 Two-way video testimony related to certain forensic medical examinations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-188.4.md","filePath":"vacode/19.2-188.4.md"}'),n={name:"vacode/19.2-188.4.md"},o=i('<h1 id="§-19-2-188-4-two-way-video-testimony-related-to-certain-forensic-medical-examinations" tabindex="-1">§ 19.2-188.4 Two-way video testimony related to certain forensic medical examinations. <a class="header-anchor" href="#§-19-2-188-4-two-way-video-testimony-related-to-certain-forensic-medical-examinations" aria-label="Permalink to &quot;§ 19.2-188.4 Two-way video testimony related to certain forensic medical examinations.&quot;">​</a></h1><p>A. Any testimony offered by either party in a preliminary hearing or sentencing hearing, or offered by the accused in any hearing other than a trial, by a sexual assault nurse examiner or sexual assault forensic examiner who performed a forensic medical examination may be presented using two-way video conferencing.</p><p>B. Any testimony offered by either party in a trial, or offered by the attorney for the Commonwealth in any hearing other than a preliminary hearing or sentencing hearing, by a sexual assault nurse examiner or sexual assault forensic examiner who performed a forensic medical examination may be presented by two-way video conferencing with the consent of the court and all parties.</p><p>C. The two-way video testimony permitted by this section shall comply with the provisions of subsection B of § <a href="/vacode/19.2-3.1/">19.2-3.1</a>. In addition, unless otherwise agreed to by the parties and the court, (i) all orders pertaining to witnesses apply to witnesses testifying by two-way video conferencing; (ii) upon request, all materials read or used by the witness during his testimony shall be identified on the video; and (iii) any witness testifying by two-way video conferencing shall certify at the conclusion of his testimony, under penalty of perjury, that he did not engage in any off-camera communications with any person during his testimony.</p><p>D. Nothing in this section shall be construed as requiring a locality to purchase a two-way electronic video and audio communication system. Any decision to purchase such a system is at the discretion of the locality.</p><p>2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0253" target="_blank" rel="noreferrer">253</a>.</p>',6),r=[o];function s(c,d,l,h,y,m){return t(),a("div",null,r)}const u=e(n,[["render",s]]);export{p as __pageData,u as default};
