import{_ as t,o as r,c as o,z as e,a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 19.2-207 Composition of a special grand jury.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-207.md","filePath":"vacode/19.2-207.md"}'),n={name:"vacode/19.2-207.md"},i=e("h1",{id:"§-19-2-207-composition-of-a-special-grand-jury",tabindex:"-1"},[a("§ 19.2-207 Composition of a special grand jury. "),e("a",{class:"header-anchor",href:"#§-19-2-207-composition-of-a-special-grand-jury","aria-label":'Permalink to "§ 19.2-207 Composition of a special grand jury."'},"​")],-1),s=e("p",null,"Special grand juries shall consist of not less than seven and not more than 11 members, and shall be summoned from a list prepared by the court. Members of a special grand jury shall possess the same qualifications as those prescribed for members of a regular grand jury, including indifferent in the cause to be conducted by the special grand jury. In order to determine a potential juror's qualifications, the presiding judge shall examine each juror individually and under oath. He shall then certify in writing and not under seal that he has examined the members of the special grand jury and has found that they are qualified and are impartial and disinterested in the subject matter and outcome of the investigation. The examination shall be recorded by a court reporter and conducted pursuant to the requirements of secrecy provided for in this chapter. The court shall appoint one of the members as foreman.",-1),d=e("p",null,[a("1975, c. 495; 2008, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0644",target:"_blank",rel:"noreferrer"},"644"),a(".")],-1),c=[i,s,d];function l(h,p,u,m,f,g){return r(),o("div",null,c)}const y=t(n,[["render",l]]);export{b as __pageData,y as default};
