import{_ as t,o as i,c as o,z as e,a as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 19.2-215.2 Application for such grand jury.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-215.2.md","filePath":"vacode/19.2-215.2.md"}'),a={name:"vacode/19.2-215.2.md"},r=e("h1",{id:"§-19-2-215-2-application-for-such-grand-jury",tabindex:"-1"},[n("§ 19.2-215.2 Application for such grand jury. "),e("a",{class:"header-anchor",href:"#§-19-2-215-2-application-for-such-grand-jury","aria-label":'Permalink to "§ 19.2-215.2 Application for such grand jury."'},"​")],-1),s=e("p",null,"Provided the Attorney General has approved the application in writing prior to submission, application for a multi-jurisdiction grand jury may be made to the Supreme Court of Virginia by two or more attorneys for the Commonwealth from jurisdictions which would be within the original scope of the investigation. The application shall be in writing and shall state (i) which jurisdictions will be involved in the original scope of the investigation, (ii) in which jurisdiction it is requested that the multi-jurisdiction grand jury be convened, (iii) the name or names of the attorneys for the Commonwealth or their assistants who will serve as special counsel to the grand jury, (iv) the name of the attorney who shall direct the grand jury proceedings. The presiding judge may extend or limit the jurisdictional territory of the investigation, for good cause shown, upon the motion of a grand jury already convened. Notice of every such application shall be given to the attorneys for the Commonwealth in the jurisdictions named in the application and, if the original scope of the investigation is extended into other jurisdictions, notice of such extension shall be given to the attorneys for the Commonwealth in the jurisdictions into which the investigation is extended.",-1),h=e("p",null,"1983, c. 543.",-1),c=[r,s,h];function d(l,p,u,m,f,g){return i(),o("div",null,c)}cons