import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 19.2-218.1 Preliminary hearings involving certain sexual crimes against spouses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-218.1.md","filePath":"vacode/19.2-218.1.md"}'),i={name:"vacode/19.2-218.1.md"},n=t('<h1 id="§-19-2-218-1-preliminary-hearings-involving-certain-sexual-crimes-against-spouses" tabindex="-1">§ 19.2-218.1 Preliminary hearings involving certain sexual crimes against spouses. <a class="header-anchor" href="#§-19-2-218-1-preliminary-hearings-involving-certain-sexual-crimes-against-spouses" aria-label="Permalink to &quot;§ 19.2-218.1 Preliminary hearings involving certain sexual crimes against spouses.&quot;">​</a></h1><p>A. In any preliminary hearing of a charge for a violation under § <a href="http://law.lis.virginia.gov/vacode/18.2-61/" target="_blank" rel="noreferrer">18.2-61</a>, <a href="http://law.lis.virginia.gov/vacode/18.2-67.1/" target="_blank" rel="noreferrer">18.2-67.1</a>, or <a href="http://law.lis.virginia.gov/vacode/18.2-67.2/" target="_blank" rel="noreferrer">18.2-67.2</a> where the complaining witness is the spouse of the accused, upon a finding of probable cause the court may request that its court services unit, in consultation with any appropriate social services organization, local community services board, or other community mental health services organization, prepare a report analyzing the feasibility of providing counseling or other forms of therapy for the accused and the probability such treatment will be successful. Based upon this report and any other relevant evidence, the court may, with the consent of the accused, the complaining witness and the attorney for the Commonwealth in any case involving a violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-61/" target="_blank" rel="noreferrer">18.2-61</a>, <a href="http://law.lis.virginia.gov/vacode/18.2-67.1/" target="_blank" rel="noreferrer">18.2-67.1</a>, or <a href="http://law.lis.virginia.gov/vacode/18.2-67.2/" target="_blank" rel="noreferrer">18.2-67.2</a>, authorize the accused to submit to and complete a designated course of counseling or therapy. In such case, the hearing shall be adjourned until such time as counseling or therapy is completed or terminated. Upon the completion of counseling or therapy by the accused and after consideration of a final evaluation to be furnished to the court by the person responsible for conducting such counseling or therapy and such further report of the court services unit as the court may require, and after consideration of the views of the complaining witness, the court, in its discretion, may discharge the accused if the court finds such action will promote maintenance of the family unit and be in the best interest of the complaining witness.</p><p>B. No statement or disclosure by the accused concerning the alleged offense made during counseling or any other form of therapy ordered pursuant to this section or § <a href="http://law.lis.virginia.gov/vacode/18.2-61/" target="_blank" rel="noreferrer">18.2-61</a>, <a href="http://law.lis.virginia.gov/vacode/18.2-67.1/" target="_blank" rel="noreferrer">18.2-67.1</a>, <a href="http://law.lis.virginia.gov/vacode/18.2-67.2/" target="_blank" rel="noreferrer">18.2-67.2</a>, or <a href="http://law.lis.virginia.gov/vacode/19.2-218.2/" target="_blank" rel="noreferrer">19.2-218.2</a> may be used against the accused in any trial as evidence, nor shall any evidence against the accused be admitted which was discovered through such statement or disclosure.</p><p>1986, c. 516; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0631" target="_blank" rel="noreferrer">631</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>.</p>',4),o=[n];function s(c,l,h,g,u,d){return a(),r("div",null,o)}const v=e(i,[["render",s]]);export{p as __pageData,v as default};
