import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 19.2-218.2 Hearing before juvenile and domestic relations district court required for persons accused of certain violations against their spouses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-218.2.md","filePath":"vacode/19.2-218.2.md"}'),a={name:"vacode/19.2-218.2.md"},o=i('<h1 id="§-19-2-218-2-hearing-before-juvenile-and-domestic-relations-district-court-required-for-persons-accused-of-certain-violations-against-their-spouses" tabindex="-1">§ 19.2-218.2 Hearing before juvenile and domestic relations district court required for persons accused of certain violations against their spouses. <a class="header-anchor" href="#§-19-2-218-2-hearing-before-juvenile-and-domestic-relations-district-court-required-for-persons-accused-of-certain-violations-against-their-spouses" aria-label="Permalink to &quot;§ 19.2-218.2 Hearing before juvenile and domestic relations district court required for persons accused of certain violations against their spouses.&quot;">​</a></h1><p>A. In any case involving a violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-61/" target="_blank" rel="noreferrer">18.2-61</a>, <a href="http://law.lis.virginia.gov/vacode/18.2-67.1/" target="_blank" rel="noreferrer">18.2-67.1</a>, or <a href="http://law.lis.virginia.gov/vacode/18.2-67.2/" target="_blank" rel="noreferrer">18.2-67.2</a> where the complaining witness is the spouse of the accused, where a preliminary hearing pursuant to § <a href="http://law.lis.virginia.gov/vacode/19.2-218.1/" target="_blank" rel="noreferrer">19.2-218.1</a> has not been held prior to indictment or trial, the court shall refer the case to the appropriate juvenile and domestic relations district court for a hearing to determine whether counseling or therapy is appropriate prior to further disposition unless the hearing is waived in writing by the accused. The court conducting this hearing may order counseling or therapy for the accused in compliance with the guidelines set forth in § <a href="http://law.lis.virginia.gov/vacode/19.2-218.1/" target="_blank" rel="noreferrer">19.2-218.1</a>.</p><p>B. After such hearing pursuant to which the accused has completed counseling or therapy and upon the recommendation of the juvenile and domestic relations district court judge conducting the hearing, the judge of the circuit court may dismiss the charge with the consent of the attorney for the Commonwealth and if the court finds such action will promote maintenance of the family unit and be in the best interest of the complaining witness.</p><p>1986, c. 516; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0631" target="_blank" rel="noreferrer">631</a>.</p>',4),n=[o];function s(c,h,d,l,u,p){return t(),r("div",null,n)}const v=e(a,[["render",s]]);export{g as __pageData,v as default};
