import{_ as t,o,c as n,z as e,a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 19.2-223 Charging several acts of embezzlement; description of money.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-223.md","filePath":"vacode/19.2-223.md"}'),r={name:"vacode/19.2-223.md"},s=e("h1",{id:"§-19-2-223-charging-several-acts-of-embezzlement-description-of-money",tabindex:"-1"},[a("§ 19.2-223 Charging several acts of embezzlement; description of money. "),e("a",{class:"header-anchor",href:"#§-19-2-223-charging-several-acts-of-embezzlement-description-of-money","aria-label":'Permalink to "§ 19.2-223 Charging several acts of embezzlement; description of money."'},"​")],-1),i=e("p",null,"In a prosecution against a person accused of embezzling or fraudulently converting to his own use bullion, money, bank notes or other security for money or items of personal property subject to larceny it shall be lawful in the same indictment or accusation to charge and thereon to proceed against the accused for any number of distinct acts of such embezzlements or fraudulent conversions which may have been committed by him within six months from the first to the last of the acts charged in the indictment; and it shall be sufficient to allege the embezzlement or fraudulent conversion to be of money without specifying any particular money, gold, silver, note or security. Such allegation, so far as it regards the description of the property, shall be sustained if the accused be proved to have embezzled any bullion, money, bank note or other security for money or items of personal property subject to larceny although the particular species be not proved.",-1),c=e("p",null,"And in a prosecution for the larceny of United States currency or for obtaining United States currency by a false pretense or token, or for receiving United States currency knowing the same to have been stolen, it shall be sufficient if the accused be proved guilty of the larceny of national bank notes or United States treasury notes, certificates for either gold or silver coin, fractional coin, currency, or any other form of money issued by the United States government, or of obtaining the same by false pretense or token, or of receiving the same knowing it to have been stolen although the particular species be not proved.",-1),l=e("p",null,"Code 1950, § 19.1-168; 1960, c. 366; 1975, c. 495; 1989, c. 370.",-1),h=[s,i,c,l];function d(f,m,u,p,y,b){return o(),n("div",null,h)}const _=t(r,[["render",d]]);export{v as __pageData,_ as default};
