import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 19.2-249.2 Venue for prosecution of computer and other crimes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-249.2.md","filePath":"vacode/19.2-249.2.md"}'),a={name:"vacode/19.2-249.2.md"},i=t('<h1 id="§-19-2-249-2-venue-for-prosecution-of-computer-and-other-crimes" tabindex="-1">§ 19.2-249.2 Venue for prosecution of computer and other crimes. <a class="header-anchor" href="#§-19-2-249-2-venue-for-prosecution-of-computer-and-other-crimes" aria-label="Permalink to &quot;§ 19.2-249.2 Venue for prosecution of computer and other crimes.&quot;">​</a></h1><p>For the purpose of venue, any violation of the Virginia Computer Crimes Act (§ <a href="http://law.lis.virginia.gov/vacode/18.2-152.1/" target="_blank" rel="noreferrer">18.2-152.1</a> et seq.) or § <a href="http://law.lis.virginia.gov/vacode/18.2-386.1/" target="_blank" rel="noreferrer">18.2-386.1</a> shall be considered to have been committed in any county or city:</p><ol><li><p>In which any act was performed in furtherance of any course of conduct that violated any provision listed above;</p></li><li><p>In which the owner has his principal place of business in the Commonwealth;</p></li><li><p>In which any offender had control or possession of any proceeds of the violation or of any books, records, documents, property, financial instrument, computer software, computer program, computer data, or other material or objects that were used in furtherance of the violation;</p></li><li><p>From which, to which, or through which any access to a computer or computer network was made whether by wires, electromagnetic waves, microwaves, optics or any other means of communication;</p></li><li><p>In which the offender resides; or</p></li><li><p>In which any computer that is an object or an instrument of the violation is located at the time of the alleged offense.</p></li></ol><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0746" target="_blank" rel="noreferrer">746</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0761" target="_blank" rel="noreferrer">761</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0827" target="_blank" rel="noreferrer">827</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0423" target="_blank" rel="noreferrer">423</a>.</p>',4),n=[i];function c(s,l,h,p,f,u){return o(),r("div",null,n)}const _=e(a,[["render",c]]);export{d as __pageData,_ as default};
