import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 19.2-262 Waiver of jury trial; numbers of jurors in criminal cases; how jurors selected from panel.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-262.md","filePath":"vacode/19.2-262.md"}'),o={name:"vacode/19.2-262.md"},n=t('<h1 id="§-19-2-262-waiver-of-jury-trial-numbers-of-jurors-in-criminal-cases-how-jurors-selected-from-panel" tabindex="-1">§ 19.2-262 Waiver of jury trial; numbers of jurors in criminal cases; how jurors selected from panel. <a class="header-anchor" href="#§-19-2-262-waiver-of-jury-trial-numbers-of-jurors-in-criminal-cases-how-jurors-selected-from-panel" aria-label="Permalink to &quot;§ 19.2-262 Waiver of jury trial; numbers of jurors in criminal cases; how jurors selected from panel.&quot;">​</a></h1><p>A. In any criminal case in which trial by jury is dispensed with as provided by law, the whole matter of law and fact shall be heard and judgment given by the court. In appeals from juvenile and domestic relations district courts, the infant, through his guardian ad litem or counsel, may waive a jury.</p><p>B. Twelve persons from a panel of not less than 20 shall constitute a jury in a felony case. Seven persons from a panel of not less than 13 shall constitute a jury in a misdemeanor case.</p><p>C. The parties or their counsel, beginning with the attorney for the Commonwealth, shall alternately strike off one name from the panel until the number remaining shall be reduced to the number required for a jury.</p><p>D. In any case in which persons indicted for felony are tried jointly, if counsel or the accused are unable to agree on the full number to be stricken, or, if for any other reason counsel or the accused fail or refuse to strike off the full number of jurors allowed such party, the clerk shall place in a box ballots bearing the names of the jurors whose names have not been stricken and shall cause to be drawn from the box such number of ballots as may be necessary to complete the number of strikes allowed the party or parties failing or refusing to strike. Thereafter, if the opposing side is entitled to further strikes, they shall be made in the usual manner.</p><p>Code 1950, § 8-208.21; 1973, c. 439; 1974, c. 611; 1975, cc. 495, 578; 1979, c. 230; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0516" target="_blank" rel="noreferrer">516</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0518" target="_blank" rel="noreferrer">518</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0356" target="_blank" rel="noreferrer">356</a>.</p>',6),s=[n];function i(l,c,h,u,f,d){return r(),a("div",null,s)}const b=e(o,[["render",i]]);export{p as __pageData,b as default};
